INSERT INTO `posts` (`id`, `title`, `summary`, `content-main`, `image`, `id_poster`) VALUES
(27, 'MU: Sau 50 trận, Van Gaal ', 'Có lẽ là hơi khập khiễng khi so sánh Van Gaal với David Moyes, bởi “tulip thép” từng giành được rất nhiều thành công cùng các đội bóng lớn như Ajax, Barca, Bayern cũng như ĐT Hà Lan. Còn David Moyes thì chỉ biết đến nhiều nhất với các thất bại cùng MU sau những chiến công ở đội bóng nhỏ Everton.', '<p style="text-align:justify"><strong>Van Gaal &quot;bất t&agrave;i&quot; như David Moyes</strong></p>\r\n\r\n<p style="text-align:justify">C&oacute; lẽ l&agrave; hơi khập khiễng khi so s&aacute;nh Van Gaal với David Moyes, bởi &ldquo;tulip th&eacute;p&rdquo; từng gi&agrave;nh được rất nhiều th&agrave;nh c&ocirc;ng c&ugrave;ng c&aacute;c đội b&oacute;ng lớn như Ajax,&nbsp;Barca, Bayern cũng như ĐT H&agrave; Lan. C&ograve;n David Moyes th&igrave; chỉ biết đến nhiều nhất với c&aacute;c thất bại c&ugrave;ng&nbsp;MU&nbsp;sau những chiến c&ocirc;ng ở đội b&oacute;ng nhỏ Everton.</p>\r\n\r\n<p style="text-align:justify">Tuy nhi&ecirc;n, tr&ecirc;n một kh&iacute;a cạnh n&agrave;o đ&oacute; c&oacute; thể coi Van Gaal chẳng hơn g&igrave; David Moyes, người phải cuốn g&oacute;i khỏi Old Trafford chỉ sau chưa đầy 1 năm dẫn dắt &ldquo;Quỷ đỏ&rdquo; bởi những kết quả k&eacute;m cỏi.</p>\r\n\r\n<p style="text-align: center;">&nbsp;<img alt="MU: Sau 50 trận, Van Gaal &quot;bất tài&quot; như… David Moyes - 1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-01/1441056986-bong-da-van-gaal.jpg" style="border:0px; height:auto; max-width:100%; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:justify">Van Gaal v&agrave; David Moyes c&oacute; c&ugrave;ng 27 trận thắng sau 50 trận đầu ti&ecirc;n dẫn dắt MU</p>\r\n\r\n<p style="text-align:justify">Cụ thể ở đ&acirc;y, Van Gaal v&agrave; David Moyes c&ugrave;ng c&oacute; 27 trận thắng sau 50 trận đầu ti&ecirc;n dẫn dắt MU. Chiến lược gia người H&agrave; Lan đ&atilde; kỉ niệm trận đấu thứ 50 c&ugrave;ng &ldquo;Red Devils&rdquo; bằng trận thua muối mặt 1-2 tr&ecirc;n s&acirc;n Liberty của Swansea ở v&ograve;ng 4 giải Ngoại hạng Anh, đồng thời cũng l&agrave; lần thứ 3 li&ecirc;n tiếp &ocirc;ng để thua trước &ldquo;Thi&ecirc;n nga đen&rdquo;, con số cho thấy sự bất lực của Van Gaal trước đội b&oacute;ng của xứ Wales.</p>\r\n\r\n<p style="text-align:justify">Nếu so s&aacute;nh 50 trận đầu ti&ecirc;n giữa Van Gaal v&agrave; David Moyes, x&eacute;t về th&agrave;nh t&iacute;ch &ldquo;tulip th&eacute;p&rdquo; chỉ hơn người đồng nghiệp vỏn vẹn c&oacute; 3 trận h&ograve;a.</p>\r\n\r\n<p style="text-align:justify"><strong>Van Gaal &ldquo;đốt tiền&rdquo; giỏi gấp hơn 3 lần David Moyes</strong></p>\r\n\r\n<p style="text-align:justify">Trong gần 1 năm dẫn dắt MU, David Moyes chỉ ti&ecirc;u c&oacute; 64,4 triệu bảng để mua sắm với 2 c&aacute;i t&ecirc;n đ&aacute;ng ch&uacute; &yacute; l&agrave; Fellaini v&agrave; Juan Mata.</p>\r\n\r\n<p style="text-align:justify">C&ograve;n với Van Gaal, được ban l&atilde;nh đạo &ldquo;Quỷ đỏ&rdquo; tạo điều kiện hết mức, &ocirc;ng đ&atilde; shopping một c&aacute;ch v&ocirc; tội vạ. 6 cầu thủ m&ugrave;a h&egrave; năm ngo&aacute;i gồm: Luke Shaw, Herrera, Rojo, Daley Blind, Di Maria, Falcao &ndash; c&ograve;n ở m&ugrave;a h&egrave; năm nay l&agrave;: Schneiderlin, Schweinsteiger, Darmian, Romero (miễn ph&iacute;), Depay đ&atilde; ti&ecirc;u tốn hơn 200 triệu bảng, tức gấp hơn 3 lần những David Moyes đ&atilde; ti&ecirc;u.</p>\r\n\r\n<p style="text-align: center;"><img alt="MU: Sau 50 trận, Van Gaal &quot;bất tài&quot; như… David Moyes - 2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-01/1441056986-bong-da-van-gaal1.jpg" style="border:0px; height:auto; max-width:100%; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:justify">Van Gaal đ&atilde; ti&ecirc;u của MU hơn 200 triệu bảng v&agrave; con số n&agrave;y vẫn chưa dừng lại</p>\r\n\r\n<p style="text-align:justify">Con số n&agrave;y sẽ tiếp tục tăng l&ecirc;n trong ng&agrave;y cuối c&ugrave;ng của k&igrave; chuyển nhượng H&egrave; 2015, bởi trong cơn kh&aacute;t tiền đạo, Van Gaal đ&atilde; c&oacute; một quyết định kh&oacute; hiểu l&agrave; chi&ecirc;u mộ tiền đạo mới 19 tuổi&nbsp;Anthony Martial&nbsp;từ Monaco với mức gi&aacute; &ldquo;đi&ecirc;n rồ&rdquo;, l&ecirc;n tới 36 triệu bảng. Hiện &nbsp;Anthony Martial&nbsp;đ&atilde; tới Manchester để kiểm tra y tế, n&ecirc;n việc ho&agrave;n tất hợp đồng chỉ c&ograve;n l&agrave; thủ tục.</p>\r\n\r\n<p style="text-align:justify">Ở chiều ngược lại, Van Gaal c&ograve;n c&oacute; một h&agrave;nh động khiến c&aacute;c manucians kh&ocirc;ng h&agrave;i l&ograve;ng, đ&oacute; l&agrave; b&aacute;n tiền đạo rất được y&ecirc;u th&iacute;ch thời Sir Alex trước kia l&agrave; Chicharito cho Leverkusen với gi&aacute; 12 triệu bảng. Trong khi đ&oacute;, h&agrave;ng c&ocirc;ng MU hiện tại chỉ c&oacute;&nbsp;Rooney&nbsp;chơi phập ph&ugrave; v&agrave; Wilson c&ograve;n qu&aacute; trẻ. V&igrave; thế, c&oacute; lẽ mọi việc sẽ phụ thuộc rất nhiều v&agrave;o t&acirc;n binh Anthony Martial.</p>\r\n\r\n<p style="text-align:justify">Chuyện Van Gaal &ldquo;đốt tiền&rdquo; giỏi c&ograve;n thể hiện bằng mua đắt b&aacute;n rẻ. &ldquo;Tulip th&eacute;p&rdquo; bằng mọi gi&aacute; đ&atilde; mua Di Maria từ Real m&ugrave;a h&egrave; năm ngo&aacute;i với mức gi&aacute; kỉ lục 59,7 triệu bảng, nhưng chỉ sau đ&oacute; 1 năm đ&atilde; b&aacute;n tống b&aacute;n th&aacute;o &ldquo;Thi&ecirc;n thần&rdquo; cho PSG với gi&aacute; 44,3 triệu bảng. Tinh sơ sơ, MU đ&atilde; lỗ thương vụ n&agrave;y tới 15 triệu bảng chỉ sau c&oacute; 1 năm.</p>\r\n\r\n<p style="text-align:justify">Chẳng biết ban l&atilde;nh đạo MU t&iacute;nh to&aacute;n ra sao v&agrave; c&ograve;n đặt niềm tin v&agrave;o&nbsp;Van Gaal&nbsp;đến bao giờ, nhưng x&eacute;t về th&agrave;nh t&iacute;ch v&agrave; c&aacute;ch ti&ecirc;u tiền, &ldquo;tulip th&eacute;p&rdquo; đang g&acirc;y thất vọng tr&agrave;n trề&hellip;</p>\r\n', 'ebbd3ec750b109ce2e85a7e27e30c75e.jpg', 7),
(28, 'MU: Nghịch lý dùng người khó hiểu của Van Gaal', 'Hàng công cùn mẻ, Van Gaal lại bán tiền đạo. Hàng thủ lỏng lẻo, Van Gaal lại tống đi hậu vệ. Chưa kể Van Gaal lại đang trọng dụng Sergio Romero vừa mắc sai lầm chỉ vì xung đột với David De Gea và Victor Valdes.', '<p style="text-align:justify"><strong>H&agrave;ng c&ocirc;ng c&ugrave;n mẻ, Van Gaal lại b&aacute;n tiền đạo. H&agrave;ng thủ lỏng lẻo, Van Gaal lại tống đi hậu vệ. Chưa kể Van Gaal lại đang trọng dụng Sergio Romero vừa mắc sai lầm chỉ v&igrave; xung đột với David De Gea v&agrave; Victor Valdes.</strong></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><em>Nh&igrave;n lại m&agrave;n tr&igrave;nh diễn của Sergio Romero trước Swansea:</em></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Tờ Daily Mail mới đ&acirc;y đưa tin&nbsp;<strong>Chicharito</strong>&nbsp;đ&atilde; rời&nbsp;Manchester United&nbsp;để chuyển sang Bayer Leverkusen với gi&aacute; 12 triệu bảng v&agrave; một bản hợp đồng 3 năm. Ngay sau khi trận đấu Swansea &ndash; MU kết th&uacute;c, Chicharito đ&atilde; l&ecirc;n m&aacute;y bay tới Đức.</p>\r\n\r\n<p style="text-align:justify">Sự ra đi của tiền đạo người Mexico đến đ&uacute;ng thời điểm MU thua bạc nhược trước Swansea với tỷ số 1-2 trong một trận đấu m&agrave; h&agrave;ng c&ocirc;ng &ldquo;c&ugrave;n&rdquo; của &ldquo;Quỷ Đỏ&rdquo; đ&atilde; phải nhờ v&agrave;o Juan Mata để ghi b&agrave;n, trong khi h&agrave;ng thủ lỏng lẻo cộng th&ecirc;m những sai lầm trong xử l&yacute; của thủ m&ocirc;n Sergio Romero đ&atilde; để thủng lưới 2 b&agrave;n trong thời gian 5 ph&uacute;t.</p>\r\n\r\n<p style="text-align:justify">Trong l&uacute;c h&agrave;ng c&ocirc;ng mờ nhạt v&agrave; h&agrave;ng thủ chật vật như vậy, nhưng Louis Van Gaal kh&ocirc;ng những đ&atilde; b&aacute;n trung vệ Jonny Evans sang West Brom m&agrave; c&ograve;n sắp c&ocirc;ng bố ch&iacute;nh thức thương vụ Chicharito. Đấy l&agrave; c&ograve;n chưa n&oacute;i đến vị tr&iacute; thủ m&ocirc;n: Người đ&aacute;ng ra phải bắt dự bị (Romero) lại bắt ch&iacute;nh, c&ograve;n thủ m&ocirc;n xứng đ&aacute;ng xuất ph&aacute;t (De Gea) th&igrave; lại bị đ&agrave;y đọa v&igrave; đ&ograve;i rời&nbsp;Real Madrid.</p>\r\n\r\n<p style="text-align:center">&nbsp;<img alt="MU: Nghịch lý dùng người khó hiểu của Van Gaal - 1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-08-31/1440987439-sergio-romero-mu-swansea-24h-500.jpg" style="border:0px; height:auto; max-width:100%; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:justify">Sergio Romero để lọt lưới 2 b&agrave;n, trong đ&oacute; c&oacute; lỗi ở b&agrave;n thua thứ 2</p>\r\n\r\n<p style="text-align:justify">Sergio Romero c&oacute; một ng&agrave;y thi đấu tồi. Xử l&yacute; b&oacute;ng chậm, nhiều pha ph&aacute;t b&oacute;ng l&ecirc;n thiếu ch&iacute;nh x&aacute;c, l&oacute;ng ng&oacute;ng khi phải xử l&yacute; b&oacute;ng bằng ch&acirc;n v&agrave; phản ứng chậm trong b&agrave;n thua thứ hai. Nếu như David De Gea v&agrave;o s&acirc;n, c&oacute; lẽ anh sẽ cản ph&aacute; được b&agrave;n thua đầu ti&ecirc;n v&agrave; &iacute;t nhất kh&ocirc;ng mắc sai lầm trong b&agrave;n thua thứ hai như Romero. Thậm ch&iacute; Victor Valdes cũng sẽ kh&ocirc;ng để thua 2 b&agrave;n dễ d&agrave;ng như vậy (!?). Nhưng tại sao De Gea v&agrave; Valdes kh&ocirc;ng được thi đấu th&igrave; ai cũng r&otilde;, De Gea muốn về Real như kh&ocirc;ng được c&ograve;n Valdes xung đột với Louis Van Gaal v&igrave; một l&yacute; do chẳng ai hiểu nổi.</p>\r\n\r\n<p style="text-align:justify">Nhưng&nbsp;<em>Sergio Romero</em>&nbsp;kh&ocirc;ng thể bị đổ lỗi cho to&agrave;n bộ thất bại. H&agrave;ng thủ MU nh&igrave;n chung đ&atilde; chơi thất vọng m&agrave; trường hợp của Daley Blind l&agrave; một v&iacute; dụ, Blind được tin tưởng cho vị tr&iacute; trung vệ v&agrave; đ&atilde; chơi chắc ở v&agrave;i trận đầu, nhưng lại kh&ocirc;ng k&egrave;m được Bafetimbi Gomis. Matteo Darmian cũng c&oacute; một trận đấu k&eacute;m hơn thường lệ b&ecirc;n c&aacute;nh phải. Chưa kể khi Romero thủng lưới đầu ti&ecirc;n, pha b&oacute;ng xuất ph&aacute;t từ đường chuyền hỏng của&nbsp;Rooney&nbsp;trong một pha tấn c&ocirc;ng (Rooney vẫn đang bận k&ecirc;u trời trong l&uacute;c Swansea tổ chức phản c&ocirc;ng nhanh).</p>\r\n\r\n<p style="text-align:justify">V&agrave; n&oacute;i đến Rooney l&agrave; phải n&oacute;i đến h&agrave;ng c&ocirc;ng chơi nhạt nh&ograve;a. Rooney s&uacute;t chỉ 3 lần trong trận đấu v&agrave; MU phải nhờ tới Juan Mata để mở tỷ số. Thời đỉnh cao của Rooney tay săn b&agrave;n đ&atilde; tr&ocirc;i qua từ l&acirc;u, giờ đ&acirc;y &ldquo;g&atilde; Shrek&rdquo; chỉ ph&ugrave; hợp cho vai tr&ograve; một hộ c&ocirc;ng v&agrave; CLB cần phải c&oacute; một tiền đạo đ&iacute;ch thực.</p>\r\n\r\n<p style="text-align:center">&nbsp;<img alt="MU: Nghịch lý dùng người khó hiểu của Van Gaal - 2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-08-31/1440987495-rooney-mu-swansea-2015-24h-500.jpg" style="border:0px; height:auto; max-width:100%; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:justify">Wayne Rooney lại tiếp tục im tiếng ở Premier League</p>\r\n\r\n<p style="text-align:justify">Louis Van Gaal vẫn chưa chịu thay đổi &yacute; kiến về h&agrave;ng tiền đạo của CLB. &Ocirc;ng vẫn cho Rooney l&agrave;m trung phong cắm v&agrave; vị tr&iacute; của R10 c&agrave;ng được gia cố khi Chicharito bị đẩy đi, để lại mỗi James Wilson mới 19 tuổi l&agrave;m dự bị. Chicharito thực ra l&agrave; một tiền đạo t&agrave;i năng v&agrave; c&oacute; thể tỏa s&aacute;ng nếu được trọng dụng đ&uacute;ng c&aacute;ch, nhưng Van Gaal gần như chẳng cho &ldquo;đậu nhỏ&rdquo; d&ugrave; chỉ một cơ hội trong thời gian &ocirc;ng ở Old Trafford.</p>\r\n\r\n<p style="text-align:justify">Kh&oacute; c&oacute; thể hiểu được triết l&yacute; x&acirc;y dựng đội b&oacute;ng m&agrave;&nbsp;Louis Van Gaal&nbsp;đang đặt ra ở MU. &Ocirc;ng b&aacute;n tiền đạo khi h&agrave;ng c&ocirc;ng đang chơi tồi, b&aacute;n hậu vệ trong l&uacute;c h&agrave;ng thủ thể hiện k&eacute;m, bất h&ograve;a với 2 thủ m&ocirc;n giỏi để d&ugrave;ng một thủ m&ocirc;n trung b&igrave;nh vừa mắc sai lầm. Louis Van Gaal đang đề cao mục ti&ecirc;u chiến thắng hay đặt sự phục t&ugrave;ng của c&aacute;c cầu thủ l&ecirc;n h&agrave;ng đầu? Nếu l&agrave; vế sau th&igrave; MU đ&atilde; chọn nhầm HLV rồi.</p>\r\n', 'ce9c6c8761a65687248351a7695daeb8.jpg', 7),
(37, 'Tắc đường hàng cây số, dân Thủ đô "chôn chân" trong mưa lạnh', 'Trận mưa lớn sáng nay (8.9) đúng vào giờ người dân đi làm, khiến nhiều tuyến đường bị ùn tắc nghiêm trọng. Dòng người phải "chôn chân" trong mưa lạnh hàng tiếng đồng hồ, nhiều người leo vỉa hè để “thoát thân”.', '<p style="text-align: justify;"><strong>Trận mưa lớn s&aacute;ng nay (8.9) đ&uacute;ng v&agrave;o giờ người d&acirc;n đi l&agrave;m, khiến nhiều tuyến đường bị &ugrave;n tắc nghi&ecirc;m trọng. D&ograve;ng người phải &quot;ch&ocirc;n ch&acirc;n&quot; trong mưa lạnh h&agrave;ng tiếng đồng hồ, nhiều người leo vỉa h&egrave; để &ldquo;tho&aacute;t th&acirc;n&rdquo;.</strong></p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-1.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Khoảng 8 giờ s&aacute;ng 8.9, d&ograve;ng người lưu th&ocirc;ng tr&ecirc;n đường Nhổn (H&agrave; Nội) phải ch&ocirc;n ch&acirc;n trong mưa lạnh.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-5.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Do dọc tuyến đường n&agrave;y đang thi c&ocirc;ng c&ocirc;ng tr&igrave;nh đường sắt tr&ecirc;n cao n&ecirc;n l&ograve;ng đường bị thu hẹp. Nhiều người leo l&ecirc;n vỉa h&egrave; để &ldquo;tho&aacute;t th&acirc;n&rdquo; v&igrave; sợ muộn giờ l&agrave;m.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 3" class="news-image" id="news-image-id-2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-6.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Xe m&aacute;y luồn l&aacute;ch v&agrave;o l&agrave;n &ocirc; t&ocirc;.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 4" class="news-image" id="news-image-id-3" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-7.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;Điểm n&uacute;t giao th&ocirc;ng qua Cầu Diễn &ugrave;n tắc k&eacute;o d&agrave;i cả c&acirc;y số. Xe m&aacute;y, &ocirc; t&ocirc;, xe bu&yacute;t&hellip; nối đu&ocirc;i nhau th&agrave;nh h&agrave;ng d&agrave;i.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 5" class="news-image" id="news-image-id-4" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-8.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">D&ograve;ng người phải ch&ocirc;n ch&acirc;n trong cơn mưa mỗi l&uacute;c một đ&ocirc;ng hơn.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 6" class="news-image" id="news-image-id-5" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-9.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Nhiều người gọi điện đến cơ quan xin đi l&agrave;m muộn v&igrave; tắc đường.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 7" class="news-image" id="news-image-id-6" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-10.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;Một chiến sĩ c&ocirc;ng an quận Bắc Từ Li&ecirc;m đứng giữa trời mưa ph&acirc;n l&agrave;n để giảm tải &ugrave;n tắc điểm ng&atilde; 3 Kiều Mai &ndash; Nhổn.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 8" class="news-image" id="news-image-id-7" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-11.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;Tr&ecirc;n tuyến đường Liễu Giai, Văn Cao (quận Ba Đ&igrave;nh) h&agrave;ng ng&agrave;n phương tiện cũng phải ch&ocirc;n ch&acirc;n trong mưa lớn.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 9" class="news-image" id="news-image-id-8" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-12.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">&Ocirc; t&ocirc; nối th&agrave;nh h&agrave;ng d&agrave;i tr&ecirc;n phố Trần Đăng Ninh (quận Cầu Giấy).</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 10" class="news-image" id="news-image-id-9" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-15.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Đường Xu&acirc;n Thủy &ugrave;n tắc k&eacute;o d&agrave;i từ cổng Đại học Quốc gia đến phố Phan Văn Trường.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 11" class="news-image" id="news-image-id-10" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441680769-1441680579-16.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Điểm n&uacute;t &ugrave;n tắc tại đầu đường Xu&acirc;n Thủy gần ch&acirc;n cầu Mai Dịch.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 12" class="news-image" id="news-image-id-11" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441682564-1441682198-anh-1.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Khoảng 8h s&aacute;ng, giao th&ocirc;ng tr&ecirc;n tuyến đường Trần Duy Hưng bị &ugrave;n tắc, c&aacute;c phương tiện di chuyển kh&oacute; khăn.</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 13" class="news-image" id="news-image-id-12" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441682564-1441682198-anh-3.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Tại phố Th&aacute;i H&agrave;, h&agrave;ng ng&agrave;n phương tiện nối đu&ocirc;i nhau</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 14" class="news-image" id="news-image-id-13" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441682564-1441682345-anh-4.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;Khu vực đường Nguyễn Tr&atilde;i, một phần l&ograve;ng được được qu&acirc;y lại để phục vụ cho việc thi c&ocirc;ng đường sắt tr&ecirc;n cao n&ecirc;n đ&atilde; xảy &ugrave;n tắc, c&aacute;c phương tiện kẹt cứng, di chuyển chậm</p>\r\n\r\n<p style="text-align: center;"><img alt="Tắc đường hàng cây số, dân Thủ đô &amp;#34;chôn chân&amp;#34; trong mưa lạnh - 15" class="news-image" id="news-image-id-14" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441682565-1441682370-anh-12.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">&nbsp;&Ocirc; t&ocirc;, xe m&aacute;y chen ch&uacute;c nhau di chuyển l&ecirc;n cầu vượt Ng&atilde; Tư Sở.</p>\r\n', '889b9574d2f89dcdd4a0b94507736ada.jpg', 7),
(38, 'Tuyển Đài Loan – ĐT Việt Nam: Vớt vát niềm tin', 'Đội tuyển Việt Nam đang tự tin hướng đến chiến thắng trước Đài Loan (Trung Quốc).', '<p><strong>Đội tuyển Việt Nam đang tự tin hướng đến chiến thắng trước Đ&agrave;i Loan (Trung Quốc).</strong></p>\r\n\r\n<p style="text-align:justify">Sau buổi tập đầu ti&ecirc;n của&nbsp;<strong>ĐT Việt Nam</strong>&nbsp;tr&ecirc;n s&acirc;n của Trung t&acirc;m đ&agrave;o tạo trẻ VFF trong đợt tập trung chuẩn bị cho trận đấu với Đ&agrave;i Loan ở v&ograve;ng loại World Cup 2018, HLV Miura lần đầu ti&ecirc;n l&ecirc;n tiếng chỉ tr&iacute;ch VFF về s&acirc;n tập qu&aacute; xấu đến nỗi &ldquo;Messi cũng kh&ocirc;ng đ&aacute; được tr&ecirc;n s&acirc;n n&agrave;y&rdquo;.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Khi C&ocirc;ng Vinh v&agrave; đồng đội đặt ch&acirc;n đến Đ&agrave;i Bắc (Đ&agrave;i Loan, Trung Quốc), &ocirc;ng thầy người Nhật Bản tiếp tục c&oacute; những ph&agrave;n n&agrave;n điều kiện s&acirc;n b&atilde;i m&agrave; nước chủ nh&agrave; bố tr&iacute; cho ĐT Việt Nam.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Việc &ocirc;ng Miura bắt đầu biết ch&ecirc;, biết ph&agrave;n n&agrave;n với truyền th&ocirc;ng cho thấy c&oacute; thể HLV người Nhật Bản đang gặp phải những &aacute;p lực kh&ocirc;ng nhỏ sau hơn 1 năm ngồi ghế n&oacute;ng ở ĐT Việt Nam.</p>\r\n\r\n<p style="text-align:center"><img alt="Tuyển Đài Loan – ĐT Việt Nam: Vớt vát niềm tin - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441642467-dt-1.jpg" style="border:0px; height:351px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:center">HLV Miura c&ugrave;ng học tr&ograve; trong buổi tập l&agrave;m quen s&acirc;n Municipal tối 7/9. Ảnh: Nhật Đo&agrave;n&nbsp;</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Khi người h&acirc;m mộ đang dần mất niềm tin với&nbsp;b&oacute;ng đ&aacute;&nbsp;nước nh&agrave; sau h&agrave;ng loạt những thất bại ở AFF Cup 2014,&nbsp;SEA Games 28, những trận cầu &quot;c&oacute; m&ugrave;i&rdquo; ở V-League v&agrave; mới nhất l&agrave; trận thua đậm 0-6 của U19 Việt Nam trước U19 Th&aacute;i Lan, &aacute;p lực về th&agrave;nh c&ocirc;ng của HLV Miura với&nbsp;<em>ĐT Việt Nam</em>c&agrave;ng lớn hơn bao giờ hết.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Đ&agrave;i Loan hiện đang xếp hạng 179 tr&ecirc;n bảng xếp hạng của FIFA v&agrave; k&eacute;m ĐT Việt Nam 27 bậc. 3 điểm c&oacute; được trước chủ nh&agrave; Đ&agrave;i Loan trong trận đấu diễn ra chiều nay kh&ocirc;ng chỉ gi&uacute;p ĐT Việt Nam tiếp tục nu&ocirc;i hy vọng tiến xa ở v&ograve;ng loại World Cup 2018, ở 2 trận đấu tiếp theo tr&ecirc;n s&acirc;n nh&agrave; trước ĐT Iraq v&agrave; Th&aacute;i Lan v&agrave;o th&aacute;ng 10.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Xa hơn, chiến thắng tưng bừng trước Đ&agrave;i Loan sẽ&nbsp;phần n&agrave;o vớt v&aacute;t ch&uacute;t niềm tin &iacute;t ỏi c&ograve;n lại của người h&acirc;m mộ với b&oacute;ng đ&aacute; Việt Nam. Ph&aacute;t biểu trong cuộc họp b&aacute;o trước trận đấu, HLV Miura tỏ ra kh&aacute; thận trọng, d&ugrave; đối thủ của ĐT Việt Nam được đ&aacute;nh gi&aacute; yếu nhất bảng F.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Nhưng ở một trận đấu m&agrave; ĐT Việt Nam buộc phải thắng, &ocirc;ng thầy người Nhật Bản c&oacute; c&aacute;i l&yacute; của m&igrave;nh. Đ&oacute; cũng l&agrave; l&yacute; do m&agrave; HLV Miura quyết định gọi l&ecirc;n tuyển nhiều c&aacute;i t&ecirc;n m&agrave; &ocirc;ng đ&atilde; hiểu v&agrave; đặt niềm tin tối đa.</p>\r\n\r\n<p style="text-align:center"><img alt="Tuyển Đài Loan – ĐT Việt Nam: Vớt vát niềm tin - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441642467-dt-3.jpg" style="border:0px; height:369px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:center">C&ocirc;ng Vinh c&ugrave;ng đồng đội tự tin lấy 3 điểm ngay tr&ecirc;n s&acirc;n của Đ&agrave;i Loan. Ảnh: Nhật Đo&agrave;n&nbsp;</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Tối qua (7/9), ĐT Việt Nam đ&atilde; c&oacute; buổi tập l&agrave;m quen s&acirc;n Municipal. D&ugrave; trời mưa kh&aacute; to, nhưng HLV Miura vẫn y&ecirc;u cầu c&aacute;c học tr&ograve; tập luyện thật sự nghi&ecirc;m t&uacute;c để c&oacute; sự chuẩn bị tốt nhất cho trận đấu với Đ&agrave;i Loan. Quan s&aacute;t buổi tập của ĐT Việt Nam, c&oacute; thể nhận thấy HLV Miura sẽ đặc biệt ch&uacute; trọng kh&acirc;u tấn c&ocirc;ng cho đội tuyển.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Nhiều khả năng C&ocirc;ng Vinh v&agrave; Văn Quyết l&agrave; bộ đ&ocirc;i đ&aacute; cao nhất tr&ecirc;n h&agrave;ng c&ocirc;ng ĐT Việt Nam. Đ&ocirc;i c&aacute;nh của ĐT Việt Nam l&agrave; 2 c&aacute;i t&ecirc;n quen thuộc Th&agrave;nh Lương &ndash; Minh Tuấn. Với phong độ kh&aacute; cao trong thời gian qua, Mai Tiến Th&agrave;nh c&oacute; thể được HLV Miura tin d&ugrave;ng ở vị tr&iacute; tiền vệ trung t&acirc;m c&ugrave;ng Ho&agrave;ng Thịnh.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Trong khi đ&oacute;, h&agrave;ng thủ của&nbsp;ĐT Việt Nam&nbsp;gồm những c&aacute;i t&ecirc;n Xu&acirc;n Th&agrave;nh, Ngọc Hải, Tiến Dũng, Thanh Hiền v&agrave; thủ th&agrave;nh Nguy&ecirc;n Mạnh.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Đội h&igrave;nh dự kiến ĐT Việt Nam:</strong>&nbsp;Nguy&ecirc;n Mạnh, Ngọc Hải, Xu&acirc;n Th&agrave;nh, Tiến Dũng, Thanh Hiền, Mai Tiến Th&agrave;nh, Ho&agrave;ng Thịnh, Th&agrave;nh Lương, Minh Tuấn, C&ocirc;ng Vinh, Văn Quyết.</p>\r\n\r\n<p style="text-align:justify"><strong>Dự đo&aacute;n ĐT Việt Nam thắng Đ&agrave;i Loan 3-1</strong></p>\r\n', 'f196a0065c405c98aa091ce75bb7e92a.jpg', 7),
(39, 'Hớ nặng Martial, MU là vua ', 'Không ít người hâm mộ MU thường chỉ trích Man City hay Chelsea là đội bóng "dùng tiền mua danh hiệu", thế nhưng kỳ thực, đội chủ sân Old Trafford mới chính là CLB tiêu xài "bạt mạng" nhất suốt những phiên chuyển nhượng ở những mùa bóng gần đây.', '<p style="text-align:justify"><strong>Kh&ocirc;ng &iacute;t người h&acirc;m mộ MU thường chỉ tr&iacute;ch Man City hay Chelsea l&agrave; đội b&oacute;ng &quot;d&ugrave;ng tiền mua danh hiệu&quot;, thế nhưng kỳ thực, đội chủ s&acirc;n Old Trafford mới ch&iacute;nh l&agrave; CLB ti&ecirc;u x&agrave;i &quot;bạt mạng&quot; nhất suốt những phi&ecirc;n chuyển nhượng ở những m&ugrave;a b&oacute;ng gần đ&acirc;y.</strong></p>\r\n\r\n<p style="text-align:justify">Theo kết quả mới c&ocirc;ng bố của c&ocirc;ng ty tư vấn Prime Time Sport ở Hội nghị b&oacute;ng đ&aacute; to&agrave;n cầu Soccerex ở Manchester th&igrave; c&aacute;c đội b&oacute;ng tại&nbsp;Premier League&nbsp;đ&atilde; chi đến 474,81 triệu bảng trong 3 m&ugrave;a giải gần đ&acirc;y&nbsp;để chi&ecirc;u mộ những t&acirc;n binh m&agrave; sau đ&oacute; c&aacute;c cầu thủ n&agrave;y chỉ được ra s&acirc;n chơi b&oacute;ng&nbsp;&iacute;t hơn 50% số trận m&agrave; đội b&oacute;ng mua họ thi đấu.</p>\r\n\r\n<p style="text-align:justify">Trong danh s&aacute;ch n&agrave;y,&nbsp;<strong>MU&nbsp;</strong>ch&iacute;nh l&agrave; đội b&oacute;ng l&atilde;ng ph&iacute; tiền bạc nhất khi đ&atilde; chi ra đến 117,61 triệu bảng tr&ecirc;n thị trường chuyển nhượng những m&ugrave;a giải gần đ&acirc;y, kể từ sau thời Sir Alex Ferguson. C&aacute;i t&ecirc;n tốn k&eacute;m nhất v&agrave; cũng g&acirc;y thất vọng nhất cho BLĐ v&agrave; người h&acirc;m mộ &quot;Quỷ đỏ th&agrave;nh Manchester&quot; ch&iacute;nh l&agrave; Angel di Maria.</p>\r\n\r\n<p style="text-align:center"><img alt="Hớ nặng Martial, MU là vua &quot;đốt tiền&quot; Ngoại hạng Anh - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441666335-bongda-nha-3.jpg" style="border:0px; height:auto; max-width:400px; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:center">Di Maria kh&ocirc;ng xứng đ&aacute;ng trở th&agrave;nh cầu thủ đắt gi&aacute; nhất giải Ngoại hạng</p>\r\n\r\n<p style="text-align:justify">Tiền vệ người Argentina n&agrave;y cập bến đội chủ s&acirc;n Old Trafford từ Real Madrid m&ugrave;a h&egrave; năm ngo&aacute;i với gi&aacute; trị l&ecirc;n tới 59,7 triệu bảng (cầu thủ đắt gi&aacute; nhất giải Ngoại hạng t&iacute;nh đến thời điểm hiện tại)&nbsp;nhưng anh chỉ ghi được vỏn vẹn 3 b&agrave;n thắng trong 27 lần ra s&acirc;n cho&nbsp;<em>MU</em>&nbsp;trước khi &quot;tẩu tho&aacute;t&quot; sang PSG ở kỳ chuyển nhượng m&ugrave;a H&egrave; năm nay với c&aacute;i gi&aacute; kh&ocirc;ng hề rẻ, 44 triệu bảng.</p>\r\n\r\n<p style="text-align:justify">Tiếp sau đ&oacute; l&agrave; Marouane Fellaini khi tiền vệ người Bỉ rời Everton để đến đội b&oacute;ng &aacute;o đỏ th&agrave;nh Manchester c&aacute;ch đ&acirc;y 2 năm với mức ph&iacute; chuyển nhượng l&agrave; 27,5 triệu bảng nhưng vẫn thi đấu rất phập ph&ugrave; ở<u>&nbsp;MU</u>.</p>\r\n\r\n<p style="text-align:justify">M&ugrave;a giải năm nay, MU tiếp tục g&acirc;y sốc với thế giới b&oacute;ng đ&aacute; khi bỏ ra đến 36 triệu bảng chỉ để mang về một tiền đạo mới 19 tuổi người Ph&aacute;p của AS Monaco với c&aacute;i t&ecirc;n lạ hoắc, Anthony Martial v&agrave; nhiều người đang nghi ngờ khả năng HLV Van Gaal lại tiếp tục khiến &quot;Quỷ đỏ&quot; hớ nặng tr&ecirc;n thị trường chuyển nhượng giống như 2 &quot;vết xe đổ&quot; khi mượn Falcao v&agrave; mua Di Maria m&ugrave;a trước.</p>\r\n\r\n<p style="text-align:center"><img alt="Hớ nặng Martial, MU là vua &quot;đốt tiền&quot; Ngoại hạng Anh - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441665533-bongda-nha-4.jpg" style="border:0px; height:auto; max-width:400px; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:center">MU bỏ ra đến 36 triệu bảng để mua về một tiền đạo v&ocirc; danh mới 19 tuổi l&agrave; Anthony Martial</p>\r\n\r\n<p style="text-align:justify">Xếp thứ 2 trong danh s&aacute;ch &quot;đốt tiền&quot; khủng khiếp nhất giải Ngoại hạn, thật ngạc nhi&ecirc;n kh&ocirc;ng phải những đại gia như&nbsp;Chelsea, Man City, Liverpool hay&nbsp;Arsenal&nbsp;m&agrave; l&agrave; Tottenham Hotspur. &quot;G&agrave; trống th&agrave;nh London&quot;&nbsp;đ&atilde; n&eacute;m v&agrave;o thị trường chuyển nhượng 3 m&ugrave;a vừa qua con số l&ecirc;n tới 100,81 triệu bảng để nhằm mục đ&iacute;ch cải thiện chất lượng đội h&igrave;nh nhằm mục đ&iacute;ch chen ch&acirc;n v&agrave;o top 4 để gi&agrave;nh suất dự Champions League. Thế nhưng mục ti&ecirc;u đ&oacute; vẫn kh&ocirc;ng th&agrave;nh c&ocirc;ng v&agrave; đội chủ s&acirc;n White Hart Lane vẫn tỏ ra kh&ocirc;ng mấy &quot;x&oacute;t tiền&quot; sau những th&agrave;nh t&iacute;ch khi&ecirc;m tốn của họ suốt những năm qua.</p>\r\n\r\n<p style="text-align: center;"><img alt="Hớ nặng Martial, MU là vua &quot;đốt tiền&quot; Ngoại hạng Anh - 3" class="news-image" id="news-image-id-2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441666335-bongda-nha-6.jpg" style="border:0px; height:auto; max-width:400px; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:center">Erik Lamela cũng l&agrave; một phi vụ thất bại của Tottenham</p>\r\n\r\n<p style="text-align:justify">Trong danh s&aacute;ch những đội b&oacute;ng phung ph&iacute; nhiều tiền bạc nhất Premier League, xếp sau MU v&agrave; Tottenham lần lượt l&agrave; 4 &quot;&ocirc;ng lớn&quot; Man City (53,33 triệu bảng), Liverpool (50,4 triệu bảng), Arsenal (34,33 triệu bảng) v&agrave; Chelsea (32,87 triệu bảng).</p>\r\n\r\n<p style="text-align:justify">Esteve Calzada, Gi&aacute;m đốc điều h&agrave;nh của Prime&nbsp;Time Sport chia sẻ quan điểm tr&ecirc;n tờ&nbsp;Sportmail: &quot;Ch&uacute;ng t&ocirc;i cần&nbsp;thời gian để theo d&otilde;i hiệu quả của những vụ chuyển nhượng&nbsp;nhưng sự thật l&agrave;&nbsp;tiền chi cho c&aacute;c cầu thủ đ&atilde; kh&ocirc;ng chơi thường xuy&ecirc;n cho CLB&nbsp;(như Manchester United) cũng chưa chắc&nbsp;mang lại&nbsp;th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p style="text-align:justify">Điều đ&oacute; kh&ocirc;ng l&agrave;m t&ocirc;i ngạc nhi&ecirc;n.&nbsp;Họ kh&ocirc;ng phải l&agrave; những người duy nhất v&agrave; t&ocirc;i nghĩ rằng trường hợp điển h&igrave;nh nhất l&agrave;&nbsp;Di Maria m&agrave; tất cả ch&uacute;ng ta c&oacute; thể đồng &yacute; l&agrave; một thất bại.&nbsp;Fellaini chưa&nbsp;phải l&agrave; một thương vụ chuyển nhượng&nbsp;thất bại, nhưng anh ta&nbsp;cũng đ&atilde; g&acirc;y&nbsp;thất vọng.&quot;&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Hớ nặng Martial, MU là vua &quot;đốt tiền&quot; Ngoại hạng Anh - 4" class="news-image" id="news-image-id-3" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441665533-bongda-nha-2.jpg" style="border:0px; height:auto; max-width:400px; vertical-align:middle" /></p>\r\n\r\n<p style="text-align:center">Top 8 CLB phung ph&iacute; tiền bạc nhất giải Ngoại hạng theo đ&aacute;nh gi&aacute; của Prime Time Sport k&egrave;m những cầu thủ m&agrave; họ mua về &quot;ph&iacute; tiền&quot; nhất</p>\r\n\r\n<p style="text-align:justify">&Ocirc;ng Calzada c&ograve;n cho rằng Man&nbsp;United đ&atilde; phải trả nhiều hơn con số 36 triệu bảng cho Monaco để sở hữu ch&acirc;n s&uacute;t mới&nbsp;19 tuổi Anthony Martial.</p>\r\n\r\n<p style="text-align:justify">&quot;Tr&ecirc;n giấy tờ, họ được trả tiền nhiều hơn gi&aacute; trị thực tế m&igrave;nh. T&ocirc;i cho rằng Martial thực ra chỉ đ&aacute;ng gi&aacute; 16 triệu bảng.&quot;</p>\r\n', '62717bc44a08aacf69f9e71e08b29426.jpg', 7),
(40, 'Hazard sa sút thảm hại: Ronaldo - Messi xa lắm', '“Hazard là cầu thủ chơi tệ nhất hôm nay”. Lời chỉ trích phũ phàng của HLV trưởng đội tuyển Bỉ Marc Wilmots dành cho Hazard vô tình khơi lại một vấn đề tưởng chừng như sắp chìm vào quên lãng. Và giờ, người ta lại lấy điều đó ra để thắc mắc: Hazard sa sút vì đâu? Hay vì chính anh?', '<p><strong>&ldquo;Hazard l&agrave; cầu thủ chơi tệ nhất h&ocirc;m nay&rdquo;. Lời chỉ tr&iacute;ch phũ ph&agrave;ng của HLV trưởng đội tuyển Bỉ Marc Wilmots d&agrave;nh cho Hazard v&ocirc; t&igrave;nh khơi lại một vấn đề tưởng chừng như sắp ch&igrave;m v&agrave;o qu&ecirc;n l&atilde;ng. V&agrave; giờ, người ta lại lấy điều đ&oacute; ra để thắc mắc: Hazard sa s&uacute;t v&igrave; đ&acirc;u? Hay v&igrave; ch&iacute;nh anh?</strong></p>\r\n\r\n<p style="text-align:justify"><strong>Hazard sa s&uacute;t v&igrave;&nbsp;Chelsea...</strong></p>\r\n\r\n<p style="text-align:justify">M&ugrave;a trước, phong độ xuất thần của&nbsp;<strong>Eden Hazard</strong>&nbsp;l&agrave; một trong những yếu tố gi&uacute;p Chelsea gi&agrave;nh chức v&ocirc; địch&nbsp;Premier League. Bản th&acirc;n cầu thủ người Bỉ cũng nhận được danh hiệu &ldquo;Cầu thủ xuất sắc nhất m&ugrave;a 2014/15&rdquo; tại Anh.</p>\r\n\r\n<p style="text-align:justify">Tuy vậy, phong độ đ&oacute;&nbsp;kh&ocirc;ng c&ograve;n được duy tr&igrave; ở m&ugrave;a n&agrave;y&nbsp;nữa. T&iacute;nh tổng cộng, Hazard đ&atilde; tịt ng&ograve;i 765 ph&uacute;t tr&ecirc;n mọi đấu trường. Chưa hết, &nbsp;tiền vệ người Bỉ cũng mới c&oacute; đ&uacute;ng 1 pha kiến tạo th&agrave;nh b&agrave;n từ đầu m&ugrave;a&nbsp;(cho Pedro ghi b&agrave;n trận thắng West Brom).</p>\r\n\r\n<p style="text-align:center"><img alt="Hazard sa sút thảm hại: Ronaldo - Messi xa lắm - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441643059-eden-hazard-603501.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Từ CLB đến ĐTQG, Hazard đang sa s&uacute;t th&ecirc; thảm</p>\r\n\r\n<p style="text-align:justify">Việc Hazard sa s&uacute;t l&agrave; một trong những nguy&ecirc;n nh&acirc;n lớn nhất khiến h&agrave;ng c&ocirc;ng Chelsea chơi rất bế tắc, đến nỗi HLV Jose Mourinho phải chỉ đ&iacute;ch danh cậu tr&ograve; cưng&nbsp;sau thất bại 1-2 của Chelsea trước Crystal Palace ở v&ograve;ng 4 Premier League.&nbsp;</p>\r\n\r\n<p style="text-align:justify">Bế tắc ở CLB, Hazard cũng chẳng kh&aacute; hơn khi l&ecirc;n tuyển. Bằng chứng l&agrave; trong b&agrave;i ph&aacute;t biểu sau trận thắng đảo S&iacute;p 1-0, huấn luyện vi&ecirc;n Marc Wilmots của đội tuyển Bỉ thừa nhận rất thất vọng về Hazard. Thậm ch&iacute;, chiến lược gia 46 tuổi ch&ecirc; ng&ocirc;i sao của Chelsea l&agrave; cầu thủ tệ nhất tr&ecirc;n s&acirc;n.</p>\r\n\r\n<p style="text-align:justify">&ldquo;T&ocirc;i đ&atilde; nghĩ đến chuyện thay Hazard, nhưng lại tin cậu ấy c&oacute; thể tạo ra được điều g&igrave; đ&oacute; trong khoảng 10 ph&uacute;t cuối trận. Tại World Cup, Hazard đ&atilde; bị thay ra rồi&rdquo;.</p>\r\n\r\n<p style="text-align:justify">C&oacute; người n&oacute;i&nbsp;Hazard đang mệt mỏi v&igrave;&nbsp;qu&aacute; tải - t&igrave;nh trạng chung của c&aacute;c cầu thủ Chelsea. &quot;The Blues&quot; vẫn đang l&agrave; CLB sử dụng &iacute;t cầu thủ nhất tại Premier League 2014/15, với chỉ 22 người. Điều đ&oacute; cũng c&oacute; nghĩa,&nbsp;những cầu thủ thuộc đội h&igrave;nh ch&iacute;nh &quot;The Blues&quot; phải li&ecirc;n tục c&agrave;y ải tr&ecirc;n 3,4 mặt trận.</p>\r\n\r\n<p style="text-align:justify">C&oacute; người n&oacute;i&nbsp;<em>Hazard&nbsp;</em>qu&aacute; đơn độc v&igrave;&nbsp;những vệ tinh xung quanh anh ng&agrave;y c&agrave;ng sa s&uacute;t,&nbsp;Diego Costa, Matic hay đặc biệt l&agrave; Cesc Fabregas. HLV Jose Mourinho kh&ocirc;ng phải kh&ocirc;ng nhận ra điều đ&oacute;, Dễ hiểu v&igrave; sao&nbsp;&quot;Người đặc biệt&quot;&nbsp;đ&atilde; phải sống chết đưa về Pedro Rodriguez d&ugrave;&nbsp;kh&ocirc;ng thiếu sự lựa chọn ở vị tr&iacute; tiền vệ c&aacute;nh.</p>\r\n\r\n<p style="text-align:justify"><strong>Hay v&igrave; ch&iacute;nh Hazard&hellip;</strong></p>\r\n\r\n<p style="text-align:justify">1 năm trước, trả lời cho c&acirc;u hỏi &quot;Hiện giờ anh đang ở đ&acirc;u trong bản đồ b&oacute;ng đ&aacute;&quot;, Hazard ngạo nghễ:&nbsp;&quot;T&ocirc;i thấy m&igrave;nh giỏi ngang&nbsp;<a class="TextlinkBaiviet" href="http://www.24h.com.vn/su-kien/messi/" style="text-decoration: none; color: rgb(0, 0, 255);">Messi</a>&nbsp;v&agrave; Ronaldo khi hai cầu thủ đ&oacute; ở c&ugrave;ng độ tuổi hiện tại của t&ocirc;i.&nbsp;Thứ duy nhất t&ocirc;i cần để được thừa nhận chỉ l&agrave; danh hiệu&rdquo;.</p>\r\n\r\n<p style="text-align:justify">1 năm sau, vẫn c&acirc;u hỏi ấy, nhưng&nbsp;c&acirc;u trả lời lại l&agrave;: &ldquo;T&ocirc;i lu&ocirc;n cố gắng nhưng t&ocirc;i cũng nhận ra rằng m&igrave;nh sẽ kh&ocirc;ng bao giờ trở th&agrave;nh một c&acirc;y săn b&agrave;n thực thụ, kh&ocirc;ng phải l&agrave;&nbsp;mẫu cầu thủ t&igrave;m kiếm những kỷ lục ghi b&agrave;n. Nếu t&ocirc;i c&oacute; thể ghi từ 15 đến 20 b&agrave;n/m&ugrave;a th&igrave; t&ocirc;i đ&atilde; thấy vui rồi. T&ocirc;i đang ở độ ch&iacute;n sự nghiệp nhưng t&ocirc;i vẫn muốn trở n&ecirc;n tốt hơn nữa. L&uacute;c n&agrave;y t&ocirc;i lại chưa chứng tỏ được điều đ&oacute;&rdquo;.</p>\r\n\r\n<p style="text-align:center"><img alt="Hazard sa sút thảm hại: Ronaldo - Messi xa lắm - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441643059-hazard-head1.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Bản lĩnh, sự tự tin l&agrave; thứ Hazard c&ograve;n thiếu để vươn tới tầm của Ronaldo - Messi</p>\r\n\r\n<p style="text-align:justify">Đấy,&nbsp;vấn đề lớn nhất của Hazard nằm ở đấy:&nbsp;sự tự tin. Hazard tỏa s&aacute;ng nhiều, nhưng chưa thể giữ được phong độ&nbsp;đều đặn v&agrave;&nbsp;thường bị những lời khen l&agrave;m mờ mắt. Để rồi mỗi khi gặp kh&oacute; khăn hay &aacute;p lực, anh&nbsp;chống lại một c&aacute;ch&nbsp;qu&aacute; yếu ớt b&uacute;a r&igrave;u dư luận.&nbsp;</p>\r\n\r\n<p style="text-align:justify">C&acirc;u hỏi&nbsp;tr&ecirc;n đến v&agrave;o thời điểm Chelsea c&ugrave;ng Hazard đang trải qua khoảng thời gian u &aacute;m, v&agrave; anh kh&ocirc;ng muốn &nbsp;tự tạo th&ecirc;m cho m&igrave;nh &aacute;p lực v&ocirc; h&igrave;nh n&agrave;o, hay theo một c&aacute;ch n&oacute;i kh&aacute;c, anh đang chạy trốn ch&iacute;nh m&igrave;nh.&nbsp;</p>\r\n\r\n<p style="text-align:justify">Tuổi&nbsp;24,&nbsp;Hazard kh&ocirc;ng c&ograve;n&nbsp;l&agrave; một cầu thủ trẻ để tr&ocirc;ng chờ sự bao bọc hay gi&uacute;p đỡ. Cristiano Ronaldo, Lionel Messi lu&ocirc;n trả lời mọi chỉ tr&iacute;ch bằng những b&agrave;n thắng, Ibrahimovic cũng vậy. V&agrave; giờ, đến lượt&nbsp;Hazard&nbsp;phải chứng minh rằng m&igrave;nh đủ khả năng &quot;đứng chung m&acirc;m&quot; c&ugrave;ng những người đ&agrave;n anh lẫy lừng, m&agrave;&nbsp;trước hết l&agrave;&nbsp;ở sự tự tin.&nbsp;</p>\r\n', 'eda9851d669abe93869f6255e77f460e.jpg', 7),
(41, 'Kỳ lạ Premier League: Mùa trước siêu sao, mùa này siêu tệ', 'Mới chỉ 4 vòng đấu mà “Đội hình tiêu biểu Premier League” mùa trước lại có thể trở thành “Đội hình tồi tệ nhất” mùa này. Mỗi người một cảnh. 11 cầu thủ được vinh danh cách đây 5 tháng đều đang tụt dốc đến khó tin.', '<p><strong>Mới chỉ 4 v&ograve;ng đấu m&agrave; &ldquo;Đội h&igrave;nh ti&ecirc;u biểu Premier League&rdquo; m&ugrave;a trước lại c&oacute; thể trở th&agrave;nh &ldquo;Đội h&igrave;nh tồi tệ nhất&rdquo; m&ugrave;a n&agrave;y. Mỗi người một cảnh. 11 cầu thủ được vinh danh c&aacute;ch đ&acirc;y 5 th&aacute;ng đều đang tụt dốc đến kh&oacute; tin.</strong></p>\r\n\r\n<p style="text-align:justify">Điều n&agrave;y l&agrave; dễ hiểu bởi sự khởi đầu v&ocirc; c&ugrave;ng bết b&aacute;t của&nbsp;<strong>Chelsea</strong>. Thầy tr&ograve; HLV Jose Mourinho mới chỉ gi&agrave;nh duy nhất 1 chiến thắng sau 4 v&ograve;ng đấu v&agrave; phải &ldquo;hạ m&igrave;nh&rdquo; ở vị tr&iacute; thứ 13 tr&ecirc;n bảng xếp hạng.</p>\r\n\r\n<p style="text-align:justify">Nhờ m&agrave;n tr&igrave;nh diễn ch&oacute;i s&aacute;ng m&agrave; nh&agrave; t&acirc;n v&ocirc; địch nước Anh chiếm tới hơn một nửa danh s&aacute;ch &ldquo;Đội h&igrave;nh ti&ecirc;u biểu&nbsp;Premier League&nbsp;2014-15&rdquo;. 8 cầu thủ của họ được đề cử v&agrave; 6 trong số đ&oacute; được vinh danh: Terry, Gary Cahill, Ivanovic, Matic, Diego Costa v&agrave; đặc biệt Hazard c&ograve;n gi&agrave;nh giải &ldquo;Cầu thủ xuất sắc nhất&rdquo;.</p>\r\n\r\n<p style="text-align:center"><img alt="Kỳ lạ Premier League: Mùa trước siêu sao, mùa này siêu tệ - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441649236-2b9a1b7e00000578-0-image-a-3_1441620528994.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Terry chậm chạp v&agrave; đuối sức ở tuổi 34</p>\r\n\r\n<p style="text-align:justify">Tuy nhi&ecirc;n những c&aacute;i t&ecirc;n tr&ecirc;n lại l&agrave; nỗi thất vọng suốt 1 th&aacute;ng qua của đội chủ s&acirc;n Stamford Bridge. Đội trưởng Terry lần đầu ti&ecirc;n bị thay khỏi s&acirc;n dưới triều đại Mourinho. Cahill bị Aguero &ldquo;khủng bố&rdquo; ở trận thua 0-3 tr&ecirc;n s&acirc;n của Man City v&agrave; phải ngồi dự bị ở v&ograve;ng sau.</p>\r\n\r\n<p style="text-align:justify">Ivanovic &ndash; qu&yacute; &ocirc;ng ổn định &ndash; c&oacute; lẽ trở th&agrave;nh mắt x&iacute;ch yếu nhất trong h&agrave;ng thủ của&nbsp;Chelsea&nbsp;v&agrave;o l&uacute;c n&agrave;y. Hậu vệ c&aacute;nh phải 31 tuổi n&agrave;y li&ecirc;n tục đ&oacute;ng vai &ldquo;g&atilde; hề&rdquo; bởi Montero, Sterling v&agrave; Callum McManaman.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Chốt chặn Matic cũng phải sụp đổ theo d&ugrave; anh vẫn l&agrave; cầu thủ chơi kỷ luật nhất. Điều n&agrave;y l&agrave; kh&oacute; tr&aacute;nh khỏi khi người đ&aacute; cặp Fabregas kh&ocirc;ng những chẳng trợ gi&uacute;p m&agrave; c&ograve;n trở th&agrave;nh g&aacute;nh nặng.</p>\r\n\r\n<p style="text-align:center"><img alt="Kỳ lạ Premier League: Mùa trước siêu sao, mùa này siêu tệ - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441649236-2bea8db300000578-0-image-a-1_1441620196814.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">De Gea chỉ được thi đấu khi tập trung c&ugrave;ng đội tuyển quốc gia</p>\r\n\r\n<p style="text-align:justify">Diego Costa vẫn hung hăng như thường lệ nhưng những b&agrave;n thắng th&igrave; chẳng c&ograve;n nữa. Thực tế ch&acirc;n s&uacute;t gốc Brazil đ&atilde; &ldquo;hết duy&ecirc;n&rdquo; từ cuối m&ugrave;a trước v&agrave; hiện tại mới chỉ c&oacute; 1 b&agrave;n ở chiến dịch 2015-16.&nbsp;<em>Hazard&nbsp;</em>sa s&uacute;t đến nỗi &ldquo;Người đặc biệt&rdquo; phải lớn tiếng y&ecirc;u cầu cậu học tr&ograve; cưng &ldquo;nghi&ecirc;m t&uacute;c&rdquo; ngay lập tức.</p>\r\n\r\n<table align="center" cellpadding="3" cellspacing="0" style="background-color:rgb(222, 241, 200); border:1px solid rgb(187, 187, 187); color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12px; line-height:normal; margin:5px auto; max-width:500px; text-align:justify; width:500px">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Đội h&igrave;nh ti&ecirc;u biểu Premier League m&ugrave;a trước:</strong></p>\r\n\r\n			<p>De Gea - Ivanovic, Terry, Cahill,&nbsp;Bertrand - Hazard,&nbsp;Matic, Coutinho, Sanchez - Costa, Kane</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Một cựu cầu thủ kh&aacute;c của Chelsea: Ryan Bertrand &ndash; Hậu vệ c&aacute;nh tr&aacute;i hay nhất nước Anh m&ugrave;a trước &ndash; cũng &ldquo;mất t&iacute;ch&rdquo; trong m&agrave;u &aacute;o Southampton. Nhưng đ&oacute; l&agrave; do anh phải l&ecirc;n b&agrave;n mổ hồi th&aacute;ng 7 n&ecirc;n chưa thể ra s&acirc;n trận n&agrave;o.</p>\r\n\r\n<p style="text-align:justify">Tuy nhi&ecirc;n ng&ocirc;i sao g&acirc;y thất vọng nhất của đội h&igrave;nh PFA lại l&agrave; De Gea. Thủ th&agrave;nh người T&acirc;y Ban Nha chưa xỏ găng một ph&uacute;t n&agrave;o ở m&ugrave;a n&agrave;y d&ugrave; vẫn l&agrave;nh lặn. Hy vọng chuyện n&agrave;y sẽ sớm thay đổi khi vụ chuyển nhượng của anh tới Real bị sụp đổ v&agrave;o ph&uacute;t cuối.</p>\r\n\r\n<p style="text-align:center"><img alt="Kỳ lạ Premier League: Mùa trước siêu sao, mùa này siêu tệ - 3" class="news-image" id="news-image-id-2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441649236-2bf0804e00000578-0-image-a-12_1441621376536.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Kane chơi tệ đến nỗi&nbsp;khiến fan ho&agrave;i&nbsp;nghi về t&agrave;i năng</p>\r\n\r\n<p style="text-align:justify">&ldquo;Cỗ m&aacute;y ghi b&agrave;n&rdquo; Harry Kane thậm ch&iacute; c&ograve;n khiến người h&acirc;m mộ li&ecirc;n tưởng tới ng&ocirc;i sao băng l&oacute;e s&aacute;ng rồi vụt tắt. Bất chấp l&ecirc;n b&aacute;o khẳng định m&igrave;nh kh&ocirc;ng phải mẫu cầu thủ &ldquo;ăn may&rdquo;, ng&ocirc;i sao của Tottenham vẫn chưa c&oacute; b&agrave;n n&agrave;o sau 4 v&ograve;ng đấu. Tệ hại hơn nữa l&agrave; những m&agrave;n tr&igrave;nh diễn nhạt nh&ograve;a c&ugrave;ng c&aacute;c pha bỏ lỡ kh&oacute; tin của ch&acirc;n s&uacute;t 22 tuổi n&agrave;y.</p>\r\n\r\n<p style="text-align:justify">Chỉ c&oacute;&nbsp;Coutinho của Liverpool v&agrave; Sanchez của&nbsp;Arsenal<u>&nbsp;</u>l&agrave; chấp nhận được. Coutinho vụt s&aacute;ng với c&uacute; &ldquo;n&atilde; đại b&aacute;c&rdquo; ở trận mở m&agrave;n trước Stoke. Nhưng tiền vệ Brazil lại chơi thậm tệ v&agrave; c&ograve;n nhận thẻ đỏ ở thất bại 0-3 ngay tr&ecirc;n s&acirc;n nh&agrave; trước West Ham.</p>\r\n\r\n<p style="text-align:justify">Vấn đề của Sanchez l&agrave; thể lực do anh c&oacute; qu&aacute; &iacute;t thời gian nghỉ ngơi trong m&ugrave;a h&egrave;. Chức v&ocirc; địch Copa America khiến ng&ocirc;i sao người Chile tập trung muộn so với c&aacute;c đồng đội n&ecirc;n kh&ocirc;ng kịp chuẩn bị cho m&ugrave;a giải mới.</p>\r\n', '7e7198bed1c94f217757af44e27b8d14.jpg', 7);
INSERT INTO `posts` (`id`, `title`, `summary`, `content-main`, `image`, `id_poster`) VALUES
(42, 'De Bruyne được coi như “Đấng Messi”', 'Bản tin cập nhật sáng 8/9: De Bruyne được coi như “Đấng Messi”. Ngoài ra còn có các thông tin đáng chú ý khác: Pogba ca ngợi “bạn cũ” ở MU; Barca muốn sao của Celta Vigo; De Gea “hết cửa” tới Real...', '<p><strong>Bản tin cập nhật s&aacute;ng 8/9: De Bruyne được coi như &ldquo;Đấng Messi&rdquo;. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c th&ocirc;ng tin đ&aacute;ng ch&uacute; &yacute; kh&aacute;c: Pogba ca ngợi &ldquo;bạn cũ&rdquo; ở MU; Barca muốn sao của Celta Vigo; De Gea &ldquo;hết cửa&rdquo; tới Real...</strong></p>\r\n\r\n<p style="text-align:justify"><strong>Barca được vinh danh &quot;CLB xuất sắc nhất năm&quot;</strong>&nbsp;bởi hiệp hội c&aacute;c CLB ch&acirc;u &Acirc;u (ECA). Trong buổi lễ vinh danh tối qua, Barca đ&atilde; được ECA trao tặng danh hiệu &quot;CLB xuất sắc nhất năm&quot; với th&agrave;nh t&iacute;ch đoạt c&uacute; ăn 3 (c&uacute;p C1, c&uacute;p Nh&agrave; vua, La Liga) v&agrave;o năm ngo&aacute;i.</p>\r\n\r\n<p style="text-align:justify">Ngo&agrave;i Barca, c&ograve;n c&oacute; Dnipro, &Aacute; qu&acirc;n Europa League được vinh danh ở hạng mục &quot;CLB tiến bộ nhất năm&quot;. Arsenal được trao giải ở hạng mục &quot;CLB v&igrave; cộng đồng&quot; v&igrave; những đ&oacute;ng g&oacute;p cho hệ thống đ&agrave;o tạo c&aacute;c cầu thủ trẻ ở trong v&agrave; ngo&agrave;i nước.</p>\r\n\r\n<p style="text-align:justify"><strong>Pogba ca ngợi &ldquo;bạn cũ&rdquo; ở MU.&nbsp;</strong>Ng&ocirc;i sao của Juventus d&agrave;nh những lời c&oacute; c&aacute;nh cho Martial của &ldquo;Quỷ đỏ&rdquo;. Tiền vệ người Ph&aacute;p n&oacute;i t&acirc;n binh 19 tuổi n&agrave;y l&agrave; &ldquo;t&agrave;i năng v&agrave; đầy hứa hẹn&rdquo;. Anh cũng kh&acirc;m phục Martial rất ch&iacute;n chắn d&ugrave; &iacute;t hơn m&igrave;nh 3 tuổi. Pogba tin rằng việc được r&egrave;n luyện tại m&ocirc;i trường Premier League sẽ gi&uacute;p Martial thi đấu tốt hơn trong m&ugrave;a &aacute;o đội tuyển quốc gia.</p>\r\n\r\n<p style="text-align:justify"><strong>Man City coi De Bruyne như &ldquo;Đấng Messi&rdquo;.</strong>&nbsp;Đ&oacute; l&agrave; nhận định của Jerome Boateng về thương vụ trị gi&aacute; 70 triệu euro. Hậu vệ của Bayern tin rằng tiền vệ người Bỉ sẽ phải đối mặt với rất nhiều sự kỳ vọng cũng như l&agrave; sức &eacute;p tại s&acirc;n Etihad.&nbsp; Thậm ch&iacute; De Bruyne sẽ phải tạo ra những &ldquo;điều thần kỳ&rdquo; như si&ecirc;u sao Messi l&agrave;m tại Barca.</p>\r\n\r\n<p style="text-align:center"><img alt="Tin HOT sáng 8/9: De Bruyne được coi như “Đấng Messi” - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441641574-kevin-de-bruyne-wolfsburg_1bwpswyqm7z6r19gnqahixjchh.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">De Bruyne được kỳ vọng tạo nhiều ph&eacute;p m&agrave;u như Messi</p>\r\n\r\n<p style="text-align:justify"><strong>Barca muốn sao của Celta Vigo.</strong>&nbsp;Gi&aacute;m đốc&nbsp;thể thao&nbsp;Robert Fernandez tiết lộ tiền đạo Nolito đang nằm trong tầm ngắm của đội chủ s&acirc;n Nou Camp. Ch&acirc;n s&uacute;t 28 tuổi bản địa n&agrave;y thậm ch&iacute; c&ograve;n đứng đầu trong danh s&aacute;ch mua sắm v&agrave;o th&aacute;ng Gi&ecirc;ng tới. Nolito từng chơi cho đội trẻ Barca. Anh ghi 11 b&agrave;n sau 33 trận tại Liga m&ugrave;a trước.</p>\r\n\r\n<p style="text-align:justify"><strong>Emre Can muốn được tham gia tấn c&ocirc;ng nhiều hơn.</strong>&nbsp;Tiền vệ của Liverpool tin rằng anh c&oacute; thể cải thiện khả năng ghi b&agrave;n nếu kh&ocirc;ng bị b&oacute; buộc ở vị tr&iacute; trung vệ nữa. M&ugrave;a giải đầu ti&ecirc;n tại s&acirc;n Anfield, Can chỉ ghi duy&nbsp; nhất 1 b&agrave;n sau 27 trận tại Premier League. Tuy nhi&ecirc;n tuyến giữa của đội b&oacute;ng đất Cảng đang rất chật chội với sự xuất hiện của Henderson, Milner v&agrave; Lucas Leiva.</p>\r\n\r\n<p style="text-align:justify"><strong>De Gea &ldquo;hết cửa&rdquo; tới Real.&nbsp;</strong>Gaizka Mendieta v&agrave; Van der Sar cho rằng rất kh&oacute; để thủ th&agrave;nh số 1 của MU tới được Madrid trong tương lai. Vụ chuyển nhượng của anh bị đổ bể ở những ph&uacute;t cuối c&ugrave;ng của kỳ chuyển nhượng m&ugrave;a h&egrave; do MU chậm giấy tờ. Mendieta tin mối quan hệ căng thẳng giữa 2 CLB n&agrave;y sẽ khiến họ chẳng c&ograve;n mặn m&agrave; trong việc mua b&aacute;n với nhau nữa.</p>\r\n', '9799ba6cf7a80447be7686fc02f3a222.jpg', 7),
(43, 'Xét tuyển nguyện vọng đợt 3: Vẫn còn hàng nghìn cơ hội cho thí sinh', 'Từ ngày 11-21.9, các trường ĐH, CĐ nhận đăng ký xét tuyển nguyện vọng bổ sung đợt 3. Theo Bộ GD-ĐT, đến thời điểm này, cả nước này vẫn còn 170 trường thiếu chỉ tiêu.', '<p><strong>Từ ng&agrave;y 11-21.9, c&aacute;c trường ĐH, CĐ nhận đăng k&yacute; x&eacute;t tuyển nguyện vọng bổ sung đợt 3. Theo Bộ GD-ĐT, đến thời điểm n&agrave;y, cả nước n&agrave;y vẫn c&ograve;n 170 trường thiếu chỉ ti&ecirc;u.</strong></p>\r\n\r\n<p style="text-align:justify">Kết th&uacute;c đợt x&eacute;t tuyển nguyện vọng bổ sung đợt 2, nhiều trường đại học, cao đẳng vẫn thiếu h&agrave;ng ngh&igrave;n chỉ ti&ecirc;u.</p>\r\n\r\n<p style="text-align: center;"><img alt="Xét tuyển nguyện vọng đợt 3: Vẫn còn hàng nghìn cơ hội cho thí sinh - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441683681-1441683661-nguyen-vong.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Th&iacute; sinh vẫn c&ograve;n nhiều cơ hội v&agrave;o ĐH, CĐ trong đợt x&eacute;t tuyển nguyện vọng bổ sung</p>\r\n\r\n<p style="text-align:justify">Hiện chỉ c&oacute; một số trường x&eacute;t tuyển nguyện vọng bổ sung c&oacute; số lượng hồ sơ đăng k&yacute; x&eacute;t tuyển đủ hoặc nhiều hơn so với dự kiến như: Trường Đại học Sư phạm H&agrave; Nội, Trường đại học Điện lực, Trường Đại học Văn h&oacute;a H&agrave; Nội, Học viện Thanh thiếu ni&ecirc;n&hellip; Trong khi đ&oacute;, nhiều trường c&ocirc;ng lập v&agrave; trường ngo&agrave;i c&ocirc;ng lập d&ugrave; điểm nhận hồ sơ ở mức điểm s&agrave;n nhưng vẫn thiếu nhiều chỉ ti&ecirc;u. Do đ&oacute;, c&aacute;c trường đang loay hoay t&igrave;m c&aacute;ch x&eacute;t tuyển nguyện vọng bổ sung đợt 3.</p>\r\n\r\n<p style="text-align:justify">Kết th&uacute;c đợt x&eacute;t tuyển nguyện vọng 2, l&atilde;nh đạo trường Đại học Phương Đ&ocirc;ng, PGS-TS Vũ Ph&aacute;n, Ph&oacute; Hiệu trưởng cho biết, trường nhận được khoảng 450 hồ sơ đăng k&yacute; x&eacute;t tuyển nguyện vọng bổ sung tr&ecirc;n tổng số hơn 1.300 chỉ ti&ecirc;u&nbsp;tuyển sinh. Như vậy, trường vẫn c&ograve;n cơ hội cho gần 1000 th&iacute; sinh .</p>\r\n\r\n<p style="text-align:justify">Trong khi đ&oacute;, trường Đại học Kinh doanh v&agrave; C&ocirc;ng nghệ H&agrave; Nội cho biết, trường vẫn c&ograve;n khoảng 3.000 chỉ ti&ecirc;u v&agrave; tiếp tục tuyển sinh nguyện vọng bổ sung đợt 3.</p>\r\n\r\n<p style="text-align:justify">Theo GS.TS. Vũ Văn H&oacute;a, Ph&oacute; Hiệu trưởng trường Đại học Kinh doanh v&agrave; C&ocirc;ng nghệ H&agrave; Nội, nếu trường kh&ocirc;ng đủ chỉ ti&ecirc;u sẽ đề xuất Bộ GD-ĐT cho trường x&eacute;t tuyển học bạ THPT.</p>\r\n\r\n<p style="text-align:justify">&Ocirc;ng Nguyễn Thanh Tĩnh, th&agrave;nh vi&ecirc;n Hội đồng quản trị Đại học D&acirc;n lập Đ&ocirc;ng Đ&ocirc; th&ocirc;ng tin, kết th&uacute;c đợt x&eacute;t tuyển nguyện vọng bổ sung đợt 2, trường mới nhận được 500 hồ sơ tr&ecirc;n tổng số 1.500 chỉ ti&ecirc;u bổ sung. Hiện tại, số th&iacute; sinh trường tuyển được vẫn chưa đạt 1/2 tổng chỉ ti&ecirc;u.</p>\r\n\r\n<p style="text-align:justify">C&ograve;n tại trường ĐH L&acirc;m nghiệp H&agrave; Nội, l&atilde;nh đạo nh&agrave; trường cho biết, tổng số hồ sơ trường nhận được trong đợt x&eacute;t tuyển nguyện vọng bổ sung l&agrave; 700 hồ sơ. Trong khi đ&oacute;, tổng số chỉ ti&ecirc;u nh&agrave; trường cần tuyển l&agrave; 1.800. Như vậy, nh&agrave; trường c&ograve;n thiếu đến tr&ecirc;n 1.000 chỉ ti&ecirc;u.</p>\r\n\r\n<p style="text-align:justify">Trao đổi với ph&oacute;ng vi&ecirc;n, &ocirc;ng L&ecirc; Viết Khuyến nguy&ecirc;n Vụ trưởng Vụ&nbsp;gi&aacute;o dục&nbsp;Đại học cho biết: Trong đợt x&eacute;t tuyển nguyện vọng 2 Bộ GD-ĐT đ&atilde; c&oacute; nhiều thay đổi t&iacute;ch cực. Tuy nhi&ecirc;n, hiện c&aacute;c trường đại học, cao đẳng vẫn c&oacute; nguy cơ cạn kiệt nguồn tuyển sinh.</p>\r\n\r\n<p style="text-align:justify">Theo &ocirc;ng, do Bộ gi&aacute;o dục- Đ&agrave;o tạo kh&ocirc;ng khống chế chỉ ti&ecirc;u tuyển sinh của c&aacute;c trường đại học, cao đẳng n&ecirc;n c&aacute;c trường top tr&ecirc;n sẽ đủ chỉ ti&ecirc;u, nhưng c&aacute;c trường top giữa v&agrave; top dưới sẽ kh&ocirc;ng c&oacute; th&iacute; sinh. Do đ&oacute;, c&aacute;c trường đại học, cao đẳng x&eacute;t tuyển nguyện vọng bổ sung nhận được &iacute;t hồ sơ x&eacute;t tuyển.</p>\r\n\r\n<p style="text-align:justify">Để thuận lợi cho th&iacute; sinh, Bộ GD-ĐT đ&atilde; c&oacute; c&ocirc;ng văn y&ecirc;u cầu c&aacute;c trường phải c&ocirc;ng bố kết quả tr&uacute;ng tuyển đợt 2 trước 10/9. Sau đ&oacute; từ ng&agrave;y 11-21/9, c&aacute;c trường nhận đăng k&yacute; x&eacute;t tuyển nguyện vọng bổ sung đợt 3.</p>\r\n\r\n<p style="text-align:justify">Đối với c&aacute;c trường chưa tuyển đủ chỉ ti&ecirc;u chủ động c&ocirc;ng bố thời gian nhận ĐKXT từ ng&agrave;y 25/9 đến ng&agrave;y 15/10/2015 v&agrave; c&ocirc;ng bố kết quả tr&uacute;ng tuyển trước 19/10/2015. Bộ GD-ĐT cũng cho biết,&nbsp;tuyển sinh đại học, cao đẳng sẽ kết th&uacute;c ng&agrave;y 20/10/2015.</p>\r\n', '8f506a046d7fb1fda7b539084c7bf6a4.jpg', 7),
(44, 'Hà Nội: Thanh tra đột xuất những khoản thu đầu năm học', 'Sở GD-ĐT Hà Nội cho biết, các đoàn kiểm tra sẽ tái kiểm tra đột xuất các trường nếu phát hiện các khoản thu chi đầu năm có dấu hiệu bất thường sẽ xử lý triệt để.', '<p style="text-align:justify">Sở GD-ĐT H&agrave; Nội cho biết, ng&agrave;y 14.9 đến ng&agrave;y 25.9, Sở GD-ĐT sẽ th&agrave;nh lập 20 đo&agrave;n thanh tra, kiểm tra việc thực hiện c&aacute;c khoản thu - chi v&agrave; hoạt động quản l&yacute; dạy th&ecirc;m, học th&ecirc;m đầu năm học 2015-2016 tại c&aacute;c nh&agrave; trường tr&ecirc;n địa b&agrave;n th&agrave;nh phố.</p>\r\n\r\n<p style="text-align:justify">&ldquo;Chủ trương tổ chức kiểm tra của Sở GD-ĐT năm nay l&agrave; kh&ocirc;ng chờ xảy ra vi phạm mới xử l&yacute;, m&agrave; để c&aacute;c nh&agrave; trường qu&aacute;n triệt quy định v&agrave; cam kết kh&ocirc;ng vi phạm&rdquo;, Sở GD-ĐT H&agrave; Nội cho hay.</p>\r\n\r\n<p style="text-align: center;"><img alt="Hà Nội: Thanh tra đột xuất những khoản thu đầu năm học - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441599450-1441599425-thu-phi-dau-nam.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">H&agrave; Nội sẽ kiểm tra c&aacute;c khoản thu đầu năm tại c&aacute;c trường</p>\r\n\r\n<p style="text-align:justify">Ngo&agrave;i ra, c&aacute;c đo&agrave;n kiểm tra sẽ t&aacute;i kiểm tra đột xuất c&aacute;c đơn vị nếu ph&aacute;t hiện dấu hiệu bất thường sẽ xử l&yacute; theo quy định.</p>\r\n\r\n<p style="text-align:justify">C&ocirc;ng t&aacute;c kiểm tra sẽ tập trung v&agrave;o c&aacute;c nội dung cơ bản như: Thực hiện c&aacute;c khoản thu theo quy định v&agrave; c&aacute;c khoản thu ngo&agrave;i học ph&iacute;, c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ cho học sinh kh&oacute; khăn v&agrave; việc triển khai hoạt động quản l&yacute; dạy th&ecirc;m, học th&ecirc;m theo quy định của Bộ GD-ĐT v&agrave; UBND th&agrave;nh phố H&agrave; Nội.</p>\r\n\r\n<p style="text-align:justify">Đối với việc thực hiện c&aacute;c khoản thu kh&aacute;c (khoản thu dễ nảy sinh bức x&uacute;c trong dư luận), Sở GD-ĐT y&ecirc;u cầu trong khi chưa c&oacute; văn bản của UBND th&agrave;nh phố, c&aacute;c trường sẽ thực hiện theo hướng dẫn tạm thời của Sở GD-ĐT như năm học trước.</p>\r\n\r\n<p style="text-align:justify">Theo đ&oacute;, danh mục khoản thu thỏa thuận gồm 4 khoản: Phục vụ b&aacute;n tr&uacute;, học 2 buổi/ng&agrave;y, học phẩm v&agrave; nước uống tinh khiết cho học sinh.</p>\r\n\r\n<p style="text-align:justify">Để tr&aacute;nh t&igrave;nh trạng mỗi nơi thu một kiểu, Sở GD-ĐT H&agrave; Nội cũng đ&atilde; quy định mức trần cụ thể cho từng khoản với y&ecirc;u cầu c&aacute;c nh&agrave; trường căn cứ v&agrave;o t&igrave;nh h&igrave;nh thực tế x&acirc;y dựng dự to&aacute;n chi để l&agrave;m căn cứ t&iacute;nh to&aacute;n mức thu v&agrave; phải c&oacute; văn bản thỏa thuận tới từng phụ huynh.</p>\r\n', '796c4a90c70f0da5a482a03fe506e244.jpg', 7),
(45, 'Lời khai lạnh người của kẻ giết vợ dã man vì cuồng ghen', 'Năn nỉ quay về nhà sống chung không thành, Kính và vợ xảy ra cãi vã. Lợi dụng lúc vợ đang múc nước, Khính đã ra tay sát hại người một thời "đầu gối tay ấp" bằng chày gỗ và dao.', '<p><strong>Năn nỉ quay về nh&agrave; sống chung kh&ocirc;ng th&agrave;nh, K&iacute;nh v&agrave; vợ xảy ra c&atilde;i v&atilde;. Lợi dụng l&uacute;c vợ đang m&uacute;c nước, Kh&iacute;nh đ&atilde; ra tay s&aacute;t hại người một thời &quot;đầu gối tay ấp&quot; bằng ch&agrave;y gỗ v&agrave; dao.</strong></p>\r\n\r\n<p style="text-align:justify">Th&ocirc;ng tin từ C&ocirc;ng an huyện Y&ecirc;n Th&agrave;nh (Nghệ An) cho biết, cơ quan điều tra vừa khởi tố vụ &aacute;n, khởi tố bị can v&agrave; bắt tạm giam 4 th&aacute;ng đối với Nguyễn Hữu Kh&iacute;nh (SN 1965, tr&uacute; x&oacute;m 2, x&atilde; Sơn Th&agrave;nh, Y&ecirc;n Th&agrave;nh) về h&agrave;nh vi giết người. Nạn nh&acirc;n trong vụ &aacute;n n&agrave;y ch&iacute;nh l&agrave; chị Trần Thị D (SN 1970)-vợ của Kh&iacute;nh.</p>\r\n\r\n<p style="text-align: center;"><img alt="Lời khai lạnh người của kẻ giết vợ dã man vì cuồng ghen - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441683168-giet-vo-2.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Đối tượng Kh&iacute;nh tại cơ quan điều tra</p>\r\n\r\n<p style="text-align:justify">Trước đ&oacute;, v&agrave;o khoảng 17h ng&agrave;y 2/9, người nh&agrave; h&atilde;i h&ugrave;ng ph&aacute;t hiện chị Trần Thị D. nằm chết ở nền giếng với nhiều vết đ&acirc;m ch&eacute;m tr&ecirc;n cơ thể. Người nh&agrave; lập tức tr&igrave;nh b&aacute;o sự việc đến C&ocirc;ng an x&atilde; Sơn Th&agrave;nh, C&ocirc;ng an huyện Y&ecirc;n Th&agrave;nh để điều tra l&agrave;m r&otilde;.</p>\r\n\r\n<p style="text-align:justify">V&agrave;o cuộc điều tra, cơ quan chức năng x&aacute;c định nghi phạm c&oacute; li&ecirc;n quan đến vụ &aacute;n ch&iacute;nh l&agrave; Nguyễn Hữu Kh&iacute;nh- chồng của nạn nh&acirc;n. Sau khi g&acirc;y &aacute;n, Kh&iacute;nh bỏ trốn l&ecirc;n n&uacute;i.</p>\r\n\r\n<p style="text-align:justify">C&ocirc;ng an huyện Y&ecirc;n Th&agrave;nh c&ugrave;ng c&aacute;c lực lượng chức năng đ&atilde; tiến h&agrave;nh bao v&acirc;y, truy bắt nghi phạm. Đến khoảng 21h c&ugrave;ng ng&agrave;y, Nguyễn Hữu Kh&iacute;nh bị bắt giữ khi đang lẩn trốn tr&ecirc;n n&uacute;i.</p>\r\n\r\n<p style="text-align:justify">Được biết, vợ chồng Kh&iacute;nh c&oacute; 4 người con, trong đ&oacute; 2 đang đi xuất khẩu lao động ở nước ngo&agrave;i. Nguy&ecirc;n nh&acirc;n của vụ &aacute;n mạng bước đầu được x&aacute;c định l&agrave; do m&acirc;u thuẫn gia đ&igrave;nh.</p>\r\n\r\n<p style="text-align:justify">C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, trong một lần đi viện kh&aacute;m bệnh, chị D. c&oacute; gặp gỡ v&agrave; quen biết với một người đ&agrave;n &ocirc;ng t&ecirc;n T. ở x&atilde; Bảo Th&agrave;nh, huyện Y&ecirc;n Th&agrave;nh. V&igrave; qu&yacute; mến n&ecirc;n người n&agrave;y c&oacute; tặng chị D. v&agrave;i bộ quần &aacute;o. Biết chuyện, Kh&iacute;nh nổi cơn ghen v&agrave; thường xuy&ecirc;n c&atilde;i v&atilde;, đ&aacute;nh đập vợ. Mỗi lần bị chồng đ&aacute;nh đập v&igrave; ghen tu&ocirc;ng, chị n&agrave;y lại khăn g&oacute;i bỏ đến nh&agrave; mẹ đẻ ở.&nbsp;</p>\r\n\r\n<p style="text-align:justify">Mấy ng&agrave;y trước, vợ chồng chị D. xảy ra m&acirc;u thuẫn, c&atilde;i v&atilde;. D&ugrave; được con c&aacute;i khuy&ecirc;n nhủ, can ngăn nhưng chị D vẫn quyết l&agrave;m đơn xin ly dị. Trong thời gian chờ t&ograve;a xử, chị D. bỏ về nh&agrave; mẹ đẻ.</p>\r\n\r\n<p style="text-align:center"><img alt="Lời khai lạnh người của kẻ giết vợ dã man vì cuồng ghen - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441683168-giet-vo-1.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Ng&ocirc;i nh&agrave;, nơi xảy ra vụ &aacute;n mạng.</p>\r\n\r\n<p style="text-align:justify">Tại CQĐT, Kh&iacute;nh khai rằng, chiều 2/9, anh ta t&igrave;m đến nh&agrave; mẹ vợ với mục đ&iacute;ch n&oacute;i chuyện v&agrave; khuy&ecirc;n giải vợ về nh&agrave;. Tại đ&acirc;y, hai vợ chồng lại xảy ra c&atilde;i v&atilde;.</p>\r\n\r\n<p style="text-align:justify">V&igrave; qu&aacute; tức giận, lợi dụng l&uacute;c chị D. đang m&uacute;c nước, Kh&iacute;nh đ&atilde; d&ugrave;ng ch&agrave;y gỗ đ&aacute;nh v&agrave;o đầu vợ. Sau đ&oacute;, đối tượng n&agrave;y d&ugrave;ng dao đ&acirc;m, khiến chị D. tử vong tại chỗ.</p>\r\n\r\n<p style="text-align:justify">Sau khi g&acirc;y &aacute;n, Kh&iacute;nh lấy điện thoại của vợ gọi hẹn gặp t&igrave;nh địch với mục đ&iacute;ch để trả th&ugrave;. Tr&ecirc;n đường đi gặp, Kh&iacute;nh c&ograve;n dọa c&oacute; mang theo 1 quả lựu đạn.</p>\r\n\r\n<p style="text-align:justify">Vụ việc đang được CQĐT C&ocirc;ng an tỉnh Nghệ An củng cố hồ sơ v&agrave; xử l&yacute; đối tượng theo ph&aacute;p luật.</p>\r\n', 'c8120a520e8e7e2736cd1a803dc74d41.jpg', 7),
(46, 'Khởi tố nhóm 9X dùng dao âm mưu sát hại xe ôm', 'Nhóm 9X, sau khi kêu xe ôm đi vào đường vắng đã dùng dao đâm và cứa cổ người này để cướp nhưng không thành.', '<p><strong>Nh&oacute;m 9X, sau khi k&ecirc;u xe &ocirc;m đi v&agrave;o đường vắng đ&atilde; d&ugrave;ng dao đ&acirc;m v&agrave; cứa cổ người n&agrave;y để cướp nhưng kh&ocirc;ng th&agrave;nh.</strong></p>\r\n\r\n<p style="text-align:justify">Chiều 7/9, Cơ quan CSĐT C&ocirc;ng an tỉnh B&igrave;nh Dương đ&atilde; ra quyết định khởi tố&nbsp;vụ &aacute;n&nbsp;đối với Trần Xu&acirc;n Tiệu (SN 1996) v&agrave; Trương Xu&acirc;n Tư (SN 1999, c&ugrave;ng qu&ecirc; Nghệ An) về h&agrave;nh vi &ldquo;giết người, cướp t&agrave;i sản&rdquo;.</p>\r\n\r\n<p style="text-align:justify">Trước đ&oacute;, v&agrave;o ng&agrave;y 26/8, hai đối tượng tr&ecirc;n t&uacute;ng n&ecirc;n b&agrave;n bạc, thống nhất sẽ thu&ecirc; xe &ocirc;m chạy xuống B&igrave;nh Dương, đưa v&agrave;o khu vắng vẻ rồi d&ugrave;ng dao&nbsp;s&aacute;t hại t&agrave;i xế cướp t&agrave;i sản.</p>\r\n\r\n<p style="text-align:justify">V&agrave;o khoảng 18h c&ugrave;ng ng&agrave;y, Tiệu v&agrave; Tư thu&ecirc; xe &ocirc;m từ tr&ecirc;n đường Ho&agrave;ng Hoa Th&aacute;m đi B&igrave;nh Dương với gi&aacute; 300000đ. Khi đến khu vực chợ đầu mối n&ocirc;ng sản Thủ Đức th&igrave; người xe &ocirc;m n&agrave;y bảo kh&ocirc;ng biết đường v&agrave; sang nhượng cho một xe &ocirc;m kh&aacute;c (nạn nh&acirc;n) l&agrave; anh Nguyễn Văn Ph. (SN 1967, qu&ecirc; Quảng Nam; tạm tr&uacute; ngụ phường Dĩ An, thị x&atilde; Dĩ An).</p>\r\n\r\n<p style="text-align: center;"><img alt="Khởi tố nhóm 9X dùng dao âm mưu sát hại xe ôm - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-08/1441674424-sat-hai-xe-om.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Cơ quan C&ocirc;ng an đ&atilde; ra quyết định khởi tố tội danh &quot;giết người, cướp t&agrave;i sản&quot; với Tiệu v&agrave; Tư.</p>\r\n\r\n<p style="text-align:justify">Hai đối tượng tr&ecirc;n n&oacute;i với anh Ph., chở tới khu vực nh&agrave; cậu ruột ở khu phố T&acirc;n Phước, phường T&acirc;n B&igrave;nh, Dĩ An, B&igrave;nh Dương, để l&agrave;m thu&ecirc;.</p>\r\n\r\n<p style="text-align:justify">Khi lưu th&ocirc;ng tới khu vực v&ograve;ng xoay An Ph&uacute; (thị x&atilde; Thuận An, B&igrave;nh Dương), ch&uacute;ng n&oacute;i nạn nh&acirc;n chạy v&agrave;o một đoạn đường vắng thuộc khu phố T&acirc;n Phước.</p>\r\n\r\n<p style="text-align:justify">Tại đ&acirc;y, c&aacute;c đối tượng bất ngờ d&ugrave;ng dao đ&acirc;m một nh&aacute;t v&agrave;o lưng anh Ph., sau đ&oacute; lao tới &ocirc;m v&agrave; d&ugrave;ng dao định s&aacute;t hại nạn nh&acirc;n.</p>\r\n\r\n<p style="text-align:justify">Lấy hết sức b&igrave;nh sinh, anh Ph. v&ugrave;ng chạy v&agrave; truy h&ocirc; mọi người, hai đối tượng tr&ecirc;n cũng nhanh ch&oacute;ng chạy khỏi hiện trường. Tuy nhi&ecirc;n, sau đ&oacute; đ&atilde; bị lực lượng d&acirc;n ph&ograve;ng v&agrave; c&aacute;c trinh s&aacute;t truy x&eacute;t bắt được.</p>\r\n\r\n<p style="text-align:justify">Anh Ph. được đưa đi cấp cứu tại bệnh viện, hiện&nbsp;sức khỏe&nbsp;đang dần hồi phục.</p>\r\n', '7779ebf414e1614fe87f80e51401581e.jpg', 7),
(47, 'Những món ăn "nhớ muôn đời" khi du lịch Nhật Bản', 'Đất nước hoa anh đào không chỉ hấp dẫn du khách bởi phong cảnh đẹp, con người thân thiện mà đến với xứ sở này khách du lịch còn có dịp nếm thử những món ăn "nhớ muôn đời".', '<p><strong>Đất nước hoa anh đ&agrave;o kh&ocirc;ng chỉ hấp dẫn du kh&aacute;ch bởi phong cảnh đẹp, con người th&acirc;n thiện m&agrave; đến với xứ sở n&agrave;y kh&aacute;ch du lịch c&ograve;n c&oacute; dịp nếm thử những m&oacute;n ăn &quot;nhớ mu&ocirc;n đời&quot;.</strong></p>\r\n\r\n<p style="text-align:justify"><strong>Shojin-ryori (Đồ chay)</strong></p>\r\n\r\n<p style="text-align:justify">C&aacute;c m&oacute;n chay của Nhật được h&igrave;nh th&agrave;nh v&agrave; ho&agrave;n thiện qua nhiều thế kỷ. Nơi tốt nhất để thưởng thức loại đồ ăn n&agrave;y l&agrave; ở c&aacute;c đền miếu (hoặc một nh&agrave; h&agrave;ng truyền thống quanh đ&oacute;), hay một lớp học&nbsp;nấu ăn.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl9.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Oyakodon (Cơm g&agrave;)</strong></p>\r\n\r\n<p style="text-align:justify">M&oacute;n ăn&nbsp;n&agrave;y kh&aacute; đơn giản, gồm cơm, thịt g&agrave;, trứng, h&agrave;nh v&agrave; một loại nước sốt nhạt. Tuy nhi&ecirc;n, như nhiều m&oacute;n Nhật kh&aacute;c, sự đặc biệt của Oyakodon đến từ chất lượng nguy&ecirc;n liệu được sử dụng.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl10.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Okonomiyaki (B&aacute;nh x&egrave;o)</strong></p>\r\n\r\n<p style="text-align:justify">Bột đặc được trộn c&ugrave;ng bắp cải th&aacute;i nhỏ v&agrave; bất cứ loại nh&acirc;n n&agrave;o bạn th&iacute;ch, như thịt muối, hải sản, ph&ocirc; mai... Sau khi b&aacute;nh ch&iacute;n, bạn c&oacute; thể cho l&ecirc;n tr&ecirc;n sốt okonomi, mayonnaise, rong biển v&agrave; c&aacute; b&agrave;o.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 3" class="news-image" id="news-image-id-2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl11.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>B&aacute;nh b&ocirc;ng lan Kabocha</strong></p>\r\n\r\n<p style="text-align:justify">M&oacute;n b&aacute;nh n&agrave;y l&agrave; sự kết hợp ho&agrave;n hảo giữa những nguy&ecirc;n liệu Nhật (rau củ địa phương, đậu adzuki, đậu phụ) v&agrave; c&aacute;ch nướng của phương T&acirc;y, tạo ra vị ngon bất ngờ. Bạn c&oacute; thể thưởng thức m&oacute;n b&aacute;nh n&agrave;y ở cố đ&ocirc; Kyoto trong c&aacute;c qu&aacute;n c&agrave; ph&ecirc; hoặc b&aacute;nh ngọt.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 4" class="news-image" id="news-image-id-3" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl12.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Takoyaki (Bạch tuộc vi&ecirc;n)</strong></p>\r\n\r\n<p style="text-align:justify">Bạch tuộc vi&ecirc;n nướng được cho th&ecirc;m h&agrave;nh tươi, gừng muối, bột tenkasu, rong biển, v&agrave; c&aacute;c miếng bạch tuộc nhỏ. B&ecirc;n trong vi&ecirc;n thịt rất n&oacute;ng, n&ecirc;n những người ăn lần đầu kh&ocirc;ng cẩn thận dễ bị bỏng.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 5" class="news-image" id="news-image-id-4" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl13.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Đồ ngọt</strong></p>\r\n\r\n<p style="text-align:justify">C&aacute;c loại đồ ngọt của&nbsp;Nhật Bản&nbsp;v&ocirc; c&ugrave;ng phong ph&uacute; v&agrave; được n&acirc;ng l&ecirc;n th&agrave;nh một nghệ thuật, nhất l&agrave; những m&oacute;n được d&ugrave;ng trong tiệc tr&agrave;. Khi v&agrave;o một h&agrave;ng đồ ngọt ở Nhật, bạn sẽ c&oacute; cảm gi&aacute;c muốn mua tất cả mọi m&oacute;n. Từ những chiếc mochi mềm mại l&agrave;m từ bột gạo, tới những chiếc dorayaki nh&acirc;n đậu đỏ... mỗi v&ugrave;ng, mỗi khu vực lại c&oacute; những m&oacute;n với c&aacute;ch chế biến kh&aacute;c nhau.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 6" class="news-image" id="news-image-id-5" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl14.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Sushi</strong></p>\r\n\r\n<p style="text-align:justify">Sushi Nhật Bản nổi tiếng của đất nước hoa anh đ&agrave;o. Sushi l&agrave; t&ecirc;n gọi của một m&oacute;n ăn gồm cơm đ&atilde; trộn dấm kết hợp với một số loại nguy&ecirc;n liệu, phổ biến nhất l&agrave; hải sản sống.</p>\r\n\r\n<p style="text-align:justify">Ng&agrave;y nay ở bất cứ đất nước n&agrave;o bạn cũng c&oacute; thể thưởng thức sushi Nhật Bản, nhưng &ldquo;h&agrave;ng hiệu&rdquo; th&igrave; chỉ c&oacute; đến Nhật bạn mới được thưởng thức trọn vẹn, đặc biệt, bạn c&ograve;n c&oacute; thể vừa ngồi ăn vừa xem c&aacute;c đầu bếp Nhật t&agrave;i hoa trổ t&agrave;i l&agrave;m sushi. C&oacute; rất nhiều loại sushi nhưng được người d&acirc;n Nhật Bản v&agrave; kh&aacute;ch du lịch ưa th&iacute;ch nhất l&agrave; sushi băng truyền, bởi n&oacute; kh&ocirc;ng chỉ rẻ m&agrave; thực kh&aacute;ch c&ograve;n c&oacute; thể ăn bất cứ m&oacute;n sushi n&agrave;o m&igrave;nh th&iacute;ch.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 7" class="news-image" id="news-image-id-6" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl1.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Sashimi &ndash; Hải sản tươi sống</strong></p>\r\n\r\n<p style="text-align:justify">Tr&ocirc;ng th&igrave; kh&ocirc;ng kh&aacute;c g&igrave; sushi, nhưng sashimi ch&iacute;nh l&agrave; m&oacute;n qu&agrave; m&agrave; biển cả ban tặng cho người d&acirc;n Nhật Bản v&agrave; cũng l&agrave;&nbsp;m&oacute;n ăn truyền thống&nbsp;n&ecirc;n thử khi du lịch Nhật Bản. M&oacute;n ăn n&agrave;y l&agrave; tập hợp của tất cả c&aacute;c loại hải sản tươi sống nhất.</p>\r\n\r\n<p style="text-align:justify">V&igrave; sashimi l&agrave; m&oacute;n ăn sống n&ecirc;n khi đ&aacute;nh bắt hải sản từ dưới biển l&ecirc;n, ngư d&acirc;n Nhật sẽ d&ugrave;ng một thanh th&eacute;p v&agrave; xuy&ecirc;n thẳng qua đầu của những con c&aacute; để ch&uacute;ng chết ngay lập tức, sau đ&oacute; xếp nhanh v&agrave;o th&ugrave;ng đ&aacute; b&agrave;o để bảo quản. Họ cho biết, khi c&aacute; chết nhanh, cơ thể ch&uacute;ng sẽ sản sinh ra &iacute;t chất axit lactic hơn, từ đ&oacute; c&oacute; thể bảo quản được trong 10 ng&agrave;y, đồng thời khiến m&oacute;n sashimi tươi hơn, ngon hơn v&agrave; an to&agrave;n hơn.</p>\r\n\r\n<p style="text-align:justify">Người Nhật thường ăn sashimi với nước tương, wasabi, gừng dấm v&agrave; rượu sake.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 8" class="news-image" id="news-image-id-7" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl2.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Sukiyaki v&agrave; Shabu Shabu &ndash; Lẩu Nhật</strong></p>\r\n\r\n<p style="text-align:justify">Sukiyaki l&agrave; m&oacute;n ăn truyền thống thể hiện sự ấm c&uacute;ng của&nbsp;gia đ&igrave;nh&nbsp;người Nhật. Th&ocirc;ng thường v&agrave;o những ng&agrave;y cuối tuần, lễ tết gia đ&igrave;nh người Nhật sẽ tụ họp v&agrave; c&ugrave;ng nhau l&agrave;m một nồi Sukiyaki để ăn. M&oacute;n ăn n&agrave;y cũng rất phổ biến kh&ocirc;ng k&eacute;m g&igrave; sushi nhưng để thưởng thức &ldquo;h&agrave;ng hiệu&rdquo; th&igrave; bạn n&ecirc;n tới nh&agrave; của một gia đ&igrave;nh người Nhật. Bởi chỉ c&oacute; ở đ&acirc;y bạn mới biết m&oacute;n Sukiyaki thực sự l&agrave; như thế n&agrave;o.</p>\r\n\r\n<p style="text-align:justify">C&ograve;n Shabu Shabu ch&iacute;nh l&agrave; m&oacute;n lẩu b&ograve; ở Việt Nam. C&oacute; kh&aacute;c chăng th&igrave; cũng chỉ l&agrave; ở nước d&ugrave;ng v&agrave; c&aacute;ch ăn. Khi nồi nước d&ugrave;ng s&ocirc;i, thực kh&aacute;ch sẽ gắp miếng thịt b&ograve; được th&aacute;i mỏng v&agrave;o v&agrave; nhấc nhanh ra, rồi chấm v&agrave;o nước tương để thưởng thức. Điều đặc biệt của m&oacute;n lẩu n&agrave;y l&agrave; hầu như kh&ocirc;ng c&oacute; rau ăn k&egrave;m m&agrave; chỉ c&oacute; thịt b&ograve;.</p>\r\n\r\n<p style="text-align:justify">Nguy&ecirc;n liệu ch&iacute;nh của Sukiyaki l&agrave; thịt b&ograve; th&aacute;i l&aacute;t mỏng, đậu phụ non, nấm v&agrave; c&aacute;c loại rau. Nghe th&igrave; c&oacute; vẻ chẳng kh&aacute;c l&agrave; mấy so với lẩu Việt Nam, nhưng hương vị th&igrave; kh&aacute;c hẳn. Bạn n&ecirc;n thử thưởng thức m&oacute;n ăn truyền thống nổi tiếng n&agrave;y khi du lịch Nhật Bản tại c&aacute;c gia đ&igrave;nh người Nhật.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 9" class="news-image" id="news-image-id-8" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl3.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Tempura &ndash; M&oacute;n chi&ecirc;n nổi tiếng</strong></p>\r\n\r\n<p style="text-align:justify">Nếu xếp về độ nổi tiếng th&igrave; tempura, m&oacute;n ăn truyền thống kh&ocirc;ng thể bỏ qua khi du lịch Nhật bản n&agrave;y chỉ đứng sau c&oacute; sushi. Đ&acirc;y thực chất l&agrave; một m&oacute;n chi&ecirc;n, với nguy&ecirc;n liệu chủ yếu l&agrave; t&ocirc;m, c&aacute; v&agrave; rau củ, trong đ&oacute; t&ocirc;m l&agrave; phổ biến nhất.</p>\r\n\r\n<p style="text-align:justify">Mặc d&ugrave; l&agrave; m&oacute;n chi&ecirc;n, nhưng tempura của Nhật Bản rất đặc biệt, từ nguy&ecirc;n liệu tươi ngon, lớp vỏ bột đặc biệt đến nước chấm thanh nhạt đ&atilde; tạo n&ecirc;n sự kh&aacute;c biệt cho m&oacute;n chi&ecirc;n nổi tiếng n&agrave;y. Bạn n&ecirc;n thưởng thức tempura ngay khi n&oacute; đước vớt ra khỏi chảo dầu, bởi để c&agrave;ng l&acirc;u vị ngon v&agrave; độ gi&ograve;n của m&oacute;n ăn c&agrave;ng giảm.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 10" class="news-image" id="news-image-id-9" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl4.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>C&aacute;c m&oacute;n m&igrave;</strong></p>\r\n\r\n<p style="text-align:justify">Kh&ocirc;ng phải giới thiệu nhiều, chắc chắn ch&uacute;ng ta đều biết về c&aacute;c m&oacute;n m&igrave; truyền thống của Nhật Bản l&agrave; Soba, Udon, Somen v&agrave; Ramen. Soba được l&agrave;m từ bột kiều mạch, sợi nhỏ v&agrave; c&oacute; m&agrave;u x&aacute;m đặc trưng; c&ograve;n Udon được l&agrave;m từ bột m&igrave;, sợi to v&agrave; c&oacute; m&agrave;u trắng đục; somen v&agrave; ramen cũng được l&agrave;m từ bột m&igrave;, sợi mảnh hơn soba.</p>\r\n\r\n<p style="text-align:justify">C&oacute; rất nhiều kiểu chế biến cho c&aacute;c loại m&igrave; n&agrave;y, t&ugrave;y theo m&ugrave;a v&agrave; sở th&iacute;ch của người ăn. Trong đ&oacute; phải kể đến kiểu chế biến m&aacute;ngtre. V&agrave;o m&ugrave;a h&egrave;, người Nhật thường ăn m&igrave; qua một m&aacute;ng tre d&agrave;i, người l&agrave;m sẽ thả m&igrave; theo d&ograve;ng nước m&aacute;t lạnh c&oacute; sẵn trong m&aacute;ng v&agrave; khi m&igrave; chảy đến chỗ bạn th&igrave; bạn chỉ cần gắp lấy n&oacute;, chấm v&agrave;o nước sốt v&agrave; ăn l&agrave; xong.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 11" class="news-image" id="news-image-id-10" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl5.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Kamaboko &ndash; Chả c&aacute; Nhật Bản</strong></p>\r\n\r\n<p style="text-align:justify">Rất đẹp mắt, rất kh&aacute;c biệt v&agrave; ngon ch&iacute;nh l&agrave; m&oacute;n chả c&aacute; của Nhật Bản. Thật đ&aacute;ng tiếc nếu bạn bỏ qua m&oacute;n ăn truyền thống n&agrave;y khi du lịch Nhật Bản.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Những món ăn &quot;nhớ muôn đời&quot; khi du lịch Nhật Bản - 12" class="news-image" id="news-image-id-11" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441617552-mon-an-doc-dao-o-nhat-ban-dspl7.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Được l&agrave;m từ c&aacute; xay nhuyễn, sau đ&oacute; nặn th&agrave;nh những h&igrave;nh d&aacute;ng kh&aacute;c nhau với những m&agrave;u sắc sặc sỡ. Nếu kh&ocirc;ng biết, bạn sẽ tưởng ch&uacute;ng l&agrave; những miếng b&aacute;nh ngọt ngon l&agrave;nh cơ. C&aacute;ch thưởng thức chả c&aacute; Nhật Bản cũng giống như chả c&aacute; Việt Nam, đều phải r&aacute;n v&agrave;ng 2 mặt.</p>\r\n', 'dff08b190bd4287869b0b95001385e73.jpg', 7),
(48, 'Vịt om hành tây lạ miệng mà ngon', 'Món ăn chế biến không quá cầu kỳ nhưng đổi lại hương vị rất hấp dẫn bạn hãy thử nhé!', '<p><strong>M&oacute;n ăn chế biến kh&ocirc;ng qu&aacute; cầu kỳ nhưng đổi lại hương vị rất hấp dẫn bạn h&atilde;y thử nh&eacute;!</strong></p>\r\n\r\n<p style="text-align:justify"><strong>Nguy&ecirc;n liệu:</strong></p>\r\n\r\n<p style="text-align:justify">- &frac14; con vịt</p>\r\n\r\n<p style="text-align:justify">- &frac12; củ h&agrave;nh t&acirc;y; 2-3 l&aacute;t gừng; 1 củ tỏi; v&agrave;i cọng h&agrave;nh l&aacute;</p>\r\n\r\n<p style="text-align:justify">Gia vị ướp vịt:</p>\r\n\r\n<p style="text-align:justify">- 15ml x&igrave; dầu; 15ml rượu nấu ăn</p>\r\n\r\n<p style="text-align:justify">Nước sốt:</p>\r\n\r\n<p style="text-align:justify">- 30g tương đen xay (mua ở si&ecirc;u thị b&aacute;n đồ H&agrave;n); 350ml nước</p>\r\n\r\n<p style="text-align:justify">- Muối v&agrave; hạt ti&ecirc;u vừa đủ</p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--1-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:justify"><strong>C&aacute;ch l&agrave;m:</strong></p>\r\n\r\n<p style="text-align:justify">Bước 1:&nbsp;Chuẩn bị c&aacute;c nguy&ecirc;n liệu: H&agrave;nh t&acirc;y th&aacute;i m&aacute;t, tỏi gừng băm nhỏ, h&agrave;nh l&aacute; cắt kh&uacute;c</p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--3-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:justify">Bước 2:&nbsp;Vịt rửa sạch. Ướp với c&aacute;c gia vị tẩm ướp trong 20 ph&uacute;t. Để sang một b&ecirc;n. Trong chảo, cho khoảng 50ml dầu ăn v&agrave;o đun ở nhiệt độ cao. Sau đ&oacute; chi vịt v&agrave;o &aacute;o chảo mỗi b&ecirc;n 5 ph&uacute;t. Sau đ&oacute; cho vịt ra đĩa.</p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 3" class="news-image" id="news-image-id-2" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--2-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 4" class="news-image" id="news-image-id-3" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--8-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 5" class="news-image" id="news-image-id-4" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--4-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:justify">Bước 3:&nbsp;Vẫn trong chảo đ&oacute;, th&ecirc;m gừng, tỏi v&agrave; phi thơm trong 1 ph&uacute;t.</p>\r\n\r\n<p style="text-align:justify">Th&ecirc;m h&agrave;nh t&acirc;y v&agrave; tương đen, đảo đều trong 3 ph&uacute;t. Sau đ&oacute; cho vịt v&agrave;o, th&ecirc;m nước v&agrave; muối, ti&ecirc;u sao cho vừa miệng rồi đậy vung lại đun trong 45 ph&uacute;t cho vịt mềm.</p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 6" class="news-image" id="news-image-id-5" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560068-1441426243-vit-om-hanh-tay--9-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 7" class="news-image" id="news-image-id-6" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--5-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:justify">Cho vịt om h&agrave;nh t&acirc;y ra chặt miếng rồi để l&ecirc;n đĩa thưởng thức!</p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 8" class="news-image" id="news-image-id-7" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--6-.jpg" style="border:0px; max-width:400px; width:500px" /></p>\r\n\r\n<p style="text-align: center;"><img alt="Vịt om hành tây lạ miệng mà ngon - 9" class="news-image" id="news-image-id-8" src="http://24h-img.24hstatic.com/upload/3-2015/images/2015-09-07/1441560067-1441426243-vit-om-hanh-tay--7-.jpg" style="border:0px; max-width:400px" /></p>\r\n\r\n<p style="text-align:center">Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng v&agrave; ngon miệng với vịt om h&agrave;nh t&acirc;y nh&eacute;!</p>\r\n', 'c75f91d0ad47e24890a7bfc68803594d.jpg', 7);

